[{"uuid":"4bad8def-05e8-4a7c-8c0b-621e715f78ae","content":"/**************************/
/* GENERAL STYLE          */
/**************************/

body {
  max-width: 1000px;
  margin: auto;
  font-family: sans-serif;
  font-size: .95rem;
}

h1, h2, h3, h4, h5, h6 {
  color: #333;
}

h3, h4 {
  margin-top: 1.33em;
  margin-bottom: 0.5em;
}

a {
  color: #868e96;
}

.text-light {
  color: #868e96!important;
}

.text-muted {
  color: #AEA79F!important;
}

/**************************/

header {
  padding-bottom: 1rem;
  border-bottom: solid 3px #aaaaaa;
}

header > h1 {
  font-size: 200%;
  margin: 0.5rem 0 ;
  line-height: 3rem;
  vertical-align: middle;
}

header > h1 > div.headline {
  float: left;
  margin-left: 1rem;
}

header > h1 > div.headline > span.km-name {
  color: #868e96;
  font-size: 75%;
  font-style: italic;
}

header > h1 > svg {
  float: left;
  height: 40px;
  width: 50px;
  vertical-align: middle;
  margin: 0.5rem;
}

header h1::after,
header::after {
  content: "";
  display: block;
  clear: both;
}

/**************************/

dl {
  color: #868e96;
}

dl dt {
  display: inline-block;
  width: 15%;
  padding: 0.2rem 0;
  float: left;
}

dl dt::after {
  content: ":";
}

dl dd {
  display: inline-block;
  width: 85%;
  margin: 0;
  padding: 0.2rem 0;
  float: left;
  font-style: italic;
}

dl dt > span.package-id {
  font-family: monospace;
  font-style: normal;
}

/**************************/

table {
  border-spacing: 0;
}

table th {
  padding: 0.5rem;
  border-right: 1px solid #E1E1E1;
  border-bottom: 1px solid #E1E1E1;
}

table th:first-child {
  border-left: 1px solid #E1E1E1;
}

table tr:first-child td,
table tr:first-child th {
  border-top: 1px solid #E1E1E1;
}

table td {
  padding: 0.5rem;
  border-right: 1px solid #E1E1E1;
  border-bottom: 1px solid #E1E1E1;
  text-align: center;
}

table td:first-child {
  border-left: 1px solid #E1E1E1;
  text-align: left;
}

/**************************/

footer {
  padding: 0.5em 0.5em 0.5em 0.5em;
  text-align: center;
  font-size: 90%;
}

/**************************/
/* KM-SPECIFIC STYLE      */
/**************************/

.report {
  margin-bottom: 1rem;
}

/**************************/

.chapter {
  border-bottom: solid 2px #cccccc;
}

/**************************/

.question, .answer-item {
  padding: 5px 10px 25px 10px;
  border-left: 5px solid #ddd;
}

.question:last-child, .answer-item:last-child {
  padding-bottom: 5px;
}

.answer-item > .followups > .question, .questions > .question {
  border: none;
  padding-left: 0;
}

.question > h4 {
  margin-top: 0;
  margin-bottom: 0.5rem;
  font-size: 0.95rem;
  font-weight: bold;
}

.question .title .human-identifier {
  display: inline-block;
  padding: 0.1rem 0.5rem;
  margin-bottom: 0.25rem;
  margin-right: 0.25rem;
  border: 1px solid #999;
  border-radius: 0.25rem;
  color: #999;
  font-size: 0.75rem;
  vertical-align: middle;
}

.question .text {
  margin-top: 0;
  margin-bottom: 0.75rem;
}

.question > .extra-data {
  margin-bottom: 0.75rem;
}

.question b {
  font-weight: normal;
}

/**************************/

.tags, .experts, .references {
  color: #868e96;
  line-height: 1.5;
}

.tags b, .experts b, .reference b,
.tags i, .experts a, .reference a {
  vertical-align: middle;
}

.tags svg, .experts svg, .references svg {
  width: 13px;
  height: 14px;
  margin-right: 7px;
  vertical-align: middle;
  color: #495057;
}

.tag::after, .expert::after, .reference::after {
  content: ",";
}

.tag:last-of-type::after, .expert:last-of-type::after, .reference:last-of-type::after {
  content: " ";
}

/**************************/

.choices-list {
  list-style-type: none;
}

.choices-list .choice {
  font-style: italic;
  padding: 0.3em;
  display: flex;
  align-items: center;
}

.choices-list .choice.choice-unselected {
  color: gray;
}

.answer-block .answer {
  font-style: italic;
}

.answer-block .answer span {
  vertical-align: middle;
}

.answer-block .answer svg,
.no-answer svg,
.choice svg {
  height: 20px;
  width: 20px;
  margin-right: 0.3rem;
  vertical-align: middle;
}

.answer-item > .answer:first-child {
  margin-top: 0;
  margin-bottom: 25px;
}

.no-answer {
  font-weight: bold;
}

.no-answer span {
  vertical-align: middle;
}

.question.required .no-answer {
  color: #D0021B;
}

.question.optional .no-answer {
  color: #9B9B9B;
}

.integration-link {
  display: flex;
  align-items: center;
}

.integration-link img {
  max-height: 2rem;
  margin-right: .5rem;
}

.metrics > p {
  font-style: italic;
  margin-top: 0;
}
