[{"uuid":"f878f29f-c928-494e-a689-2e9c13dd2fe5","content":"@import url('https://fonts.googleapis.com/css?family=Noto+Serif:400,400i,700,700i&display=swap&subset=cyrillic,cyrillic-ext,greek,greek-ext,latin-ext,vietnamese');

/* Layout and global styles */

@page {
  margin: 2.5cm;
}

h1, h2, h3, h4, h5, h6 {
  page-break-inside: avoid !important;
  page-break-after: avoid !important;
}

html, body {
  height: 100%;
}

body {
  font-family: 'Noto Serif', serif;
  font-size: 11pt;
  counter-reset: section;
  counter-reset: question;
}

/* Sections */

section {
  margin-bottom: 4rem;
}

.dmp-section .question + .question {
  margin-top: 1cm;
}

/* First page */

header {
  padding-top: 5cm;
  width: 100vw;
  height: 24cm;
  box-sizing: border-box;
  page-break-after: always;
  position: relative;
}

header .dmp::after {
  font-size: 18pt;
  content: 'Data Management Plan';
}

h1 {
  font-size: 24pt;
}

header div {
  position: absolute;
  bottom: 0;
}

header .contact-orcid {
  white-space: nowrap;
}

header .dsw {
  margin-top: 2cm;
  text-align: center;
  font-size: 10pt;
  font-style: italic;
}

dl::after {
  content: ' ';
  display: table;
  clear: both;
}

dl dt {
  display: inline-block;
  width: 20%;
  margin: 0;
  margin-bottom: 0.5rem;
  float: left;
  white-space: nowrap;
}

dl dt::after {
  content: ":";
}

dl dd {
  display: inline-block;
  width: 80%;
  margin: 0;
  margin-bottom: 0.5rem;
  float: left;
  font-style: italic;
}

dl dd ul {
  padding: 0;
  margin: 0;
}

dl dd ul li {
  list-style: none;
}

dl dd ul li:not(:last-child) {
  margin-bottom: 0.5rem;
}

dl .contact-name {
  font-weight: bold;
}

/* Abstract */

.abstract {
  page-break-after: always;
}

.abstract p {
  text-align: justify;
}

/* custom */

.mini-orcid-icon-16 {
  background: url(data:image/png;base64,) no-repeat;
  background-size: 0.5cm 0.5cm;
  width: 0.5cm;
  height: 0.5cm;
  display: inline-block;
  margin: -2px 0 -2px 2px;
}

div#dmp-content {
  page-break-before: always;
}
","fileName":"src/style.css"},{"uuid":"aba27bab-7c58-41f3-8960-16155a32df87","content":"<div id="q-data-security" class="question">
  <div class="answer">
    {%- set risksPathPrefix = [uuids.processingCUuid, uuids.risksQUuid, uuids.risksExploreAUuid]|reply_path -%}
    {%- set sharedWorkspacePrefix = [uuids.processingCUuid, uuids.sharedWorkspaceQUuid, uuids.sharedWorkspaceYesAUuid]|reply_path -%}
    {# Basic risks #}
    {%- set risksStoreLabPath = [risksPathPrefix, uuids.risksStoreLabQUuid]|reply_path -%}
    {%- set risksStoreLabAUuid = repliesMap[risksStoreLabPath]|reply_str_value -%}
    {%- set risksCarryHomePath = [risksPathPrefix, uuids.risksCarryHomeQUuid]|reply_path -%}
    {%- set risksCarryHomeAUuid = repliesMap[risksCarryHomePath]|reply_str_value -%}
    {%- set risksCarryHomeEncPath = [risksCarryHomePath, uuids.risksCarryHomeYesAUuid, uuids.risksCarryHomeEncQUuid]|reply_path -%}
    {%- set risksCarryHomeEncAUuid = repliesMap[risksCarryHomeEncPath]|reply_str_value -%}
    {%- set risksCarryHomePassPath = [risksCarryHomePath, uuids.risksCarryHomeYesAUuid, uuids.risksCarryHomePassQUuid]|reply_path -%}
    {%- set risksCarryHomePassAUuid = repliesMap[risksCarryHomePassPath]|reply_str_value -%}
    {%- set risksCertificationsPath = [risksPathPrefix, uuids.risksCertificationsQUuid]|reply_path -%}
    {%- set risksCertificationsAUuid = repliesMap[risksCertificationsPath]|reply_str_value -%}
    {%- set risksHttpsPath = [risksPathPrefix, uuids.risksHttpsQUuid]|reply_path -%}
    {%- set risksHttpsAUuid = repliesMap[risksHttpsPath]|reply_str_value -%}
    {%- set risksInstructedPath = [risksPathPrefix, uuids.risksInstructedQUuid]|reply_path -%}
    {%- set risksInstructedAUuid = repliesMap[risksInstructedPath]|reply_str_value -%}

    {%- set homeEncrypted = risksCarryHomeEncAUuid == uuids.risksCarryHomeEncYesAUuid -%}
    {%- set homePassword = risksCarryHomePassAUuid == uuids.risksCarryHomePassYesAUuid -%}
    {%- set storeLab = risksStoreLabAUuid == uuids.risksStoreLabNoAUuid -%}

    {%- if storeLab or (risksCarryHomeAUuid == uuids.risksCarryHomeNoAUuid) or (risksCarryHomeAUuid == uuids.risksCarryHomeYesAUuid and (homeEncrypted or homePassword)) or (risksCertificationsAUuid == uuids.risksCertificationsYesAUuid) or (risksHttpsAUuid == uuids.risksHttpsYesAUuid) or (risksInstructedAUuid == uuids.risksInstructedYesAUuid) %}
      <p>
        {%- if storeLab %}
          Project members will not store data or software on computers in the lab or external hard drives connected to those computers.
        {%- endif -%}
        {%- if risksCarryHomeAUuid == uuids.risksCarryHomeNoAUuid %}
          {%- if storeLab -%}They{%- else -%}Project members{%- endif %} will not carry data with them (e.g. on laptops, USB sticks, or other external media).
        {%- elif homeEncrypted and homePassword %}
          {%- if storeLab -%}They{%- else -%}Project members{%- endif %} can carry data with them on encrypted data carriers and password-protected laptops.
        {%- elif homeEncrypted %}
          {%- if storeLab -%}They{%- else -%}Project members{%- endif %} can carry data with them on encrypted data carriers.
        {%- elif homePassword %}
          {%- if storeLab -%}They{%- else -%}Project members{%- endif %} can carry data with them on password-protected laptops.
        {%- endif -%}
        {%- if risksCertificationsAUuid == uuids.risksCertificationsYesAUuid %}
          All data centers where project data is stored carry sufficient certifications.
        {%- endif -%}
        {%- if risksHttpsAUuid == uuids.risksHttpsYesAUuid %}
          All project web services addressed via secure http (https://...).
        {%- endif -%}
        {%- if risksInstructedAUuid == uuids.risksInstructedYesAUuid %}
          Project members have been instructed about both generic and specific risks to the project.
        {%- endif -%}
      </p>
    {%- endif -%}
    {# Information loss, leak, and vandalism #}
    {%- set risksInfoLossPath = [risksPathPrefix, uuids.risksInfoLossQUuid]|reply_path -%}
    {%- set risksInfoLossAUuid = repliesMap[risksInfoLossPath]|reply_str_value -%}
    {%- set risksInfoLeakPath = [risksPathPrefix, uuids.risksInfoLeakQUuid]|reply_path -%}
    {%- set risksInfoLeakAUuid = repliesMap[risksInfoLeakPath]|reply_str_value -%}
    {%- set risksInfoVandalismPath = [risksPathPrefix, uuids.risksInfoVandalismQUuid]|reply_path -%}
    {%- set risksInfoVandalismAUuid = repliesMap[risksInfoVandalismPath]|reply_str_value -%}
    {%- set risksInfoLossTexts = {
      uuids.risksInfoLossSmallAUuid: "The possible impact to the project or organization if information is lost is small.",
      uuids.risksInfoLossLowAUuid: "The risk of information loss in the project or organization is acceptably low.",
      uuids.risksInfoLossWillAUuid: "We will mitigate information loss risk for the project or organization."
    } -%}
    {%- set risksInfoLeakTexts = {
      uuids.risksInfoLeakSmallAUuid: "The possible impact to the project or organization if information is leaked is small.",
      uuids.risksInfoLeakLowAUuid: "The risk of information leak in the project or organization is acceptably low.",
      uuids.risksInfoLeakWillAUuid: "We will mitigate information leak risk for the project or organization."
    } -%}
    {%- set risksInfoVandalismTexts = {
      uuids.risksInfoVandalismSmallAUuid: "The possible impact to the project or organization if information is vandalised is small.",
      uuids.risksInfoVandalismLowAUuid: "The risk of information vandalism in the project or organization is acceptably low.",
      uuids.risksInfoVandalismWillAUuid: "We will mitigate information vandalism risk for the project or organization."
    } -%}
    {% set risksInfoXText = [
      risksInfoLossTexts[risksInfoLossAUuid],
      risksInfoLeakTexts[risksInfoLeakAUuid],
      risksInfoVandalismTexts[risksInfoVandalismAUuid]
    ]|join(" ") %}
    {%- if risksInfoXText|length > 0 -%}
      <p>{{ risksInfoXText }}</p>
    {%- endif -%}
    {# Personal data #}
    {%- set risksPersonalDataPath = [risksPathPrefix, uuids.risksPersonalDataQUuid]|reply_path -%}
    {%- set risksPersonalDataAUuid = repliesMap[risksPersonalDataPath]|reply_str_value -%}
    {%- if risksPersonalDataAUuid == uuids.risksPersonalDataNotUsingAUuid -%}
      <p>We are not using any personal information.</p>
    {%- elif risksPersonalDataAUuid == uuids.risksPersonalDataPseudoAUuid -%}
      <p>
        All personal information will be processed in pseudonymized form only.
        {%- set risksPseudonymizationPath = [risksPersonalDataPath, uuids.risksPersonalDataPseudoAUuid, uuids.risksPseudonymizationQUuid]|reply_path -%}
        {%- set risksPseudonymizationAUuid = repliesMap[risksPseudonymizationPath]|reply_str_value -%}
        {%- if risksPseudonymizationAUuid == uuids.risksPseudonymizationInsideAUuid %}
          We pseudonymize inside the project, only limited people can access the keys.
        {%- elif risksPseudonymizationAUuid == uuids.risksPseudonymizationPartyAUuid %}
          Pseudonymization is handled by an independent party in order to allow data coupling.
        {%- elif risksPseudonymizationAUuid == uuids.risksPseudonymizationAnotherAUuid -%}
          {%- set risksPseudonymizationAnotherQUuid = [risksPseudonymizationPath, uuids.risksPseudonymizationAnotherAUuid, uuids.risksPseudonymizationAnotherQUuid]|reply_path -%}
          {%- set risksPseudonymizationAnother = repliesMap[risksPseudonymizationAnotherQUuid]|reply_str_value %}
          We have a specific way of pseudonymization{{ ": " ~ risksPseudonymizationAnother|dot if risksPseudonymizationAnother else "." }}
        {%- endif -%}
      </p>
    {%- elif risksPersonalDataAUuid == uuids.risksPersonalDataAnonymousAUuid -%}
      <p>All personal data will be collected anonymously.</p>
    {%- elif risksPersonalDataAUuid == uuids.risksPersonalDataAnonymizedAUuid -%}
      <p>All personal data will be anonymized as early as possible.</p>
    {%- endif -%}
    {# Access control in shared workspace #}
    {%- set sharedAccessControlPath = [sharedWorkspacePrefix, uuids.sharedAccessControlQUuid]|reply_path -%}
    {%- set sharedAccessControlAUuid = repliesMap[sharedAccessControlPath]|reply_str_value -%}
    {%- if sharedAccessControlAUuid == uuids.sharedAccessControlMembersRWAUuid -%}
      <p>Only all project members have read/write access to the data.</p>
    {%- elif sharedAccessControlAUuid == uuids.sharedAccessControlInstituteRAUuid -%}
      <p>Anyone in the institute has read access to the data; all project members have read/write access.</p>
    {%- elif sharedAccessControlAUuid == uuids.sharedAccessControlMembersRAUuid -%}
      <p>Only project members will have read access; only selected project members will be able to write data.</p>
    {%- elif sharedAccessControlAUuid == uuids.sharedAccessControlMembersRequestAUuid -%}
      <p>Anyone in the institute can read data, only project members can request write access.</p>
    {%- endif -%}

    {# Cold storage #}
    {%- set coldStoragePath = [uuids.preservingCUuid, uuids.coldStorageQUuid]|reply_path -%}
    {%- set coldStorageAUuid = repliesMap[coldStoragePath]|reply_str_value -%}
    {%- if coldStorageAUuid == uuids.coldStorageYesAUuid -%}
      {%- set coldSentences = [] -%}
      {%- set coldStorageRemoteDisasterPath = [coldStoragePath, uuids.coldStorageYesAUuid, uuids.coldStorageRemoteDisasterQUuid]|reply_path -%}
      {%- set coldStorageRemoteDisasterAUuid = repliesMap[coldStorageRemoteDisasterPath]|reply_str_value -%}
      {%- set coldStorageProtectPath = [coldStoragePath, uuids.coldStorageYesAUuid, uuids.coldStorageProtectQUuid]|reply_path -%}
      {%- set coldStorageProtectAUuid = repliesMap[coldStorageProtectPath]|reply_str_value -%}

      {%- if coldStorageRemoteDisasterAUuid == uuids.coldStorageRemoteDisasterYesAUuid -%}
        {%- do coldSentences.append("The archive will be stored in a remote location to protect the data against disasters.") -%}
      {%- endif -%}

      {%- if coldStorageProtectAUuid == uuids.coldStorageProtectYesAUuid -%}
        {%- do coldSentences.append("The archive need to be protected against loss or theft.") -%}

        {%- set coldStorageProtectEncPath = [coldStorageProtectPath, uuids.coldStorageProtectYesAUuid, uuids.coldStorageProtectEncQUuid]|reply_path -%}
        {%- set coldStorageProtectEncAUuid = repliesMap[coldStorageProtectEncPath]|reply_str_value -%}
        {%- set coldStorageProtectPhysicalPath = [coldStorageProtectPath, uuids.coldStorageProtectYesAUuid, uuids.coldStorageProtectPhysicalQUuid]|reply_path -%}
        {%- set coldStorageProtectPhysicalAUuid = repliesMap[coldStorageProtectPhysicalPath]|reply_str_value -%}

        {%- if coldStorageRemoteDisasterAUuid == uuids.coldStorageProtectEncNoNeedAUuid -%}
          {%- do coldSentences.append("The data does not need protection in terms of encryption.") -%}
        {%- elif coldStorageRemoteDisasterAUuid == uuids.coldStorageProtectEncYesAUuid -%}

          {%- set coldStorageProtectEncKeyPath = [coldStorageProtectEncPath, uuids.coldStorageProtectEncYesAUuid, uuids.coldStorageProtectEncKeyQUuid]|reply_path -%}
          {%- set coldStorageProtectEncKeyAUuid = repliesMap[coldStorageProtectEncKeyPath]|reply_str_value -%}
          {%- if coldStorageProtectEncKeyAUuid == uuid.coldStorageProtectEncKeyYesAUuid -%}
            {%- do coldSentences.append("It will be encrypted and it is clear who has access to the key (also in case of a required data restore).") -%}
          {%- else -%}
            {%- do coldSentences.append("It will be encrypted.") -%}
          {%- endif -%}
        {%- endif -%}
        {%- if coldStorageProtectPhysicalAUuid == uuids.coldStorageProtectPhysicalYesAUuid -%}
          {%- do coldSentences.append("It is clear who has physical access to the archives.") -%}
        {%- endif -%}
      {%- endif -%}

      {%- if coldSentences|length > 0 -%}
        <p>{{ coldSentences|join(" ") }}</p>
      {%- endif -%}
    {%- endif -%}
  </div>
</div>
","fileName":"src/questions/04-data-security.html.j2"},{"uuid":"216521df-8cad-4355-a15c-002cfd05908e","content":"<div id="q-findable" class="question">
  <div class="answer">
    {# Produced datasets #}
    {%- set producedDataPath = [uuids.preservingCUuid, uuids.producedDataQUuid]|reply_path -%}
    {%- set producedDataItems = repliesMap[producedDataPath]|reply_items -%}
    {%- if producedDataItems|length > 0 -%}
      <ul>
      {%- for i in producedDataItems -%}
        {%- set pathPrefix = [producedDataPath, i]|reply_path -%}
        {%- set producedDataName = repliesMap[[pathPrefix, uuids.producedDataNameQUuid]|reply_path]|reply_str_value -%}
        <li>
          <strong>{{ producedDataName if producedDataName else "(no name given)" }}</strong>
          {# If will be published #}
          {%- set isPublishedDataPath = [pathPrefix, uuids.isPublishedDataQUuid]|reply_path -%}
          {%- set isPublishedDataAUuid = repliesMap[isPublishedDataPath]|reply_str_value -%}
          {% if isPublishedDataAUuid == uuids.isPublishedDataNoAUuid %}
            (not published)
          {% elif isPublishedDataAUuid == uuids.isPublishedDataYesAUuid %}
            (published)
          {%- set prefix = [isPublishedDataPath, uuids.isPublishedDataYesAUuid]|reply_path -%}
          {# Distributions #}
          {% set distrosPath = [prefix, uuids.publishedDistrosQUuid]|reply_path -%}
          {%- set distrosItems = repliesMap[distrosPath]|reply_items -%}
          {%- if distrosItems|length > 0 -%}
            <br>The distributions will be stored in:
            <ul>
            {%- for j in distrosItems -%}
              {%- set distroPrefix = [distrosPath, j]|reply_path -%}
              {%- set distroRepositoryKindPath = [distroPrefix, uuids.publishedDataRepositoryKindQUuid]|reply_path -%}
              {%- set distroRepositoryKindAUuid = repliesMap[distroRepositoryKindPath]|reply_str_value -%}
              {%- if distroRepositoryKindAUuid -%}
                <li>
                  {%- if distroRepositoryKindAUuid == uuids.publishedDataRepositoryDomainSpecificAUuid %}
                    Domain-specific repository
                    {%- set domainSpecificRepoNamePath = [distroRepositoryKindPath, uuids.publishedDataRepositoryDomainSpecificAUuid, uuids.domainSpecificRepoNameQUuid]|reply_path -%}
                    {%- set domainSpecificRepoNameReply = repliesMap[domainSpecificRepoNamePath] -%}
                    {%- if domainSpecificRepoNameReply -%}
                      : {{ macros.integrationValue(domainSpecificRepoNameReply, uuids.domainSpecificRepoNameQUuid) }}.
                    {%- else -%}.
                    {% endif -%}
                    {# Contact the repository #}
                    {%- set domainSpecificRepoContactBeforePath = [distroRepositoryKindPath, uuids.publishedDataRepositoryDomainSpecificAUuid, uuids.domainSpecificRepoContactBeforeQUuid]|reply_path -%}
                    {%- set domainSpecificRepoContactBeforeAUuid = repliesMap[domainSpecificRepoContactBeforePath]|reply_str_value -%}
                    {%- if domainSpecificRepoContactBeforeAUuid == uuids.domainSpecificRepoContactBeforeNoAUuid %}
                      We don't need to contact the repository because it is a routine for us.
                    {%- elif domainSpecificRepoContactBeforeAUuid == uuids.domainSpecificRepoContactBeforeYesAlreadyAUuid %}
                      We have already contacted the repository.
                    {%- elif domainSpecificRepoContactBeforeAUuid == uuids.domainSpecificRepoContactBeforeYesWillAUuid %}
                      We are going to contact the repository.
                    {%- elif domainSpecificRepoContactBeforeAUuid == uuids.domainSpecificRepoContactBeforeOtherAUuid %}
                      {%- set domainSpecificRepoContactBeforeOtherPath = [domainSpecificRepoContactBeforePath, uuids.domainSpecificRepoContactBeforeOtherAUuid, uuids.domainSpecificRepoContactBeforeOtherQUuid]|reply_path -%}
                      {%- set domainSpecificRepoContactBeforeOther = repliesMap[domainSpecificRepoContactBeforeOtherPath]|reply_str_value %}
                      We have made other arrangements{{ ": " ~ domainSpecificRepoContactBeforeOther|dot if domainSpecificRepoContactBeforeOther else "." }}
                    {%- endif -%}
                  {%- elif distroRepositoryKindAUuid == uuids.publishedDataRepositoryNationalAUuid %}
                    Our national repository.
                  {%- elif distroRepositoryKindAUuid == uuids.publishedDataRepositoryInstitutionalAUuid %}
                    Our institutional repository.
                  {%- elif distroRepositoryKindAUuid == uuids.publishedDataRepositorySpecialAUuid %}
                    Special-purpose repository for the project.
                    {# Long-term support #}
                    {%- set specialRepoLongTermSupportPath = [distroRepositoryKindPath, uuids.publishedDataRepositorySpecialAUuid, uuids.specialRepoLongTermSupportQUuid]|reply_path -%}
                    {%- set specialRepoLongTermSupportAUuid = repliesMap[specialRepoLongTermSupportPath]|reply_str_value -%}
                    {%- if specialRepoLongTermSupportAUuid == uuids.specialRepoLongTermSupportYesAUuid %}
                      We will be able to support this repository for a sufficiently long time.
                    {%- endif -%}
                    {# Service level #}
                    {%- set specialRepoServiceLevelPath = [distroRepositoryKindPath, uuids.publishedDataRepositorySpecialAUuid, uuids.specialRepoServiceLevelQUuid]|reply_path -%}
                    {%- set specialRepoServiceLevelAUuid = repliesMap[specialRepoServiceLevelPath]|reply_str_value -%}
                    {%- if specialRepoServiceLevelAUuid == uuids.specialRepoServiceLevelDownloadAUuid %}
                      The repository will provide download-only service.
                    {%- elif specialRepoServiceLevelAUuid == uuids.specialRepoServiceLevelSimpleAUuid %}
                      The repository will provice a search and simple access interface.
                    {%- elif specialRepoServiceLevelAUuid == uuids.specialRepoServiceLevelAdvancedAUuid %}
                      The repository will provide an advanced processing service.
                    {%- endif -%}
                  {%- endif -%}
                </li>
              {%- endif -%}
            {%- endfor -%}
            </ul>
          {%- endif -%}
          {# Version #}
          {%- set versionsPath = [prefix, uuids.publishedVersionsQUuid]|reply_path %}
          {%- set versionsAUuid = repliesMap[versionsPath]|reply_str_value -%}
          {%- if versionsAUuid == uuids.publishedVersionsNoAUuid -%}
            <br>There won't be different versions of this data over time.
          {%- elif versionsAUuid == uuids.publishedVersionsYesAUuid -%}
            {%- set versionsRecognizePath = [versionsPath, uuids.publishedVersionsYesAUuid, uuids.publishedVersionsRecognizeQUuid]|reply_path %}
            {%- set versionsRecognizeAUuid = repliesMap[versionsRecognizePath]|reply_str_value -%}
            {% if versionsRecognizeAUuid == uuids.publishedVersionsRecognizeDateAUuid -%}
            <br>There will be different versions of this data over time; the versions will be dated.
            {% elif versionsRecognizeAUuid == uuids.publishedVersionsRecognizeNumAUuid -%}
            <br>There will be different versions of this data over time; the versions will be numbered.
            {%- endif -%}
          {%- endif -%}
          {# Catalogue #}
          {%- set publishedDataCataloguePath = [prefix, uuids.publishedDataCatalogueQUuid]|reply_path -%}
          {%- set publishedDataCatalogueAUuid = repliesMap[publishedDataCataloguePath]|reply_str_value -%}
          {%- if publishedDataCatalogueAUuid == uuids.publishedDataCatalogueYesAUuid -%}
            <br>We will be adding a reference to the published data to at least one data catalogue.
          {%- endif -%}
          {%- endif -%}
        </li>
      {%- endfor -%}
      </ul>
    {%- endif -%}

    {# Metadata #}
    {%- set metadataPath = [uuids.creatingCUuid, uuids.metadataQUuid]|reply_path -%}
    {%- set metadataAUuid = repliesMap[metadataPath]|reply_str_value -%}
    {%- if metadataAUuid == uuids.metadataExpAUuid -%}
      {%- set prefix = [metadataPath, uuids.metadataExpAUuid]|reply_path -%}
      {# MIA #}
      {%- set metadataMiaPath = [prefix, uuids.metadataMiaQUuid]|reply_path -%}
      {%- set metadataMiaAUuid = repliesMap[metadataMiaPath]|reply_str_value -%}
      {%- if metadataMiaAUuid == uuids.metadataMiaNoAUuid -%}
        {%- set metadataMiaIdeaPath = [metadataMiaPath, uuids.metadataMiaNoAUuid, uuids.metadataMiaIdeaQUuid]|reply_path -%}
        {%- set metadataMiaIdeaAUuid = repliesMap[metadataMiaIdeaPath]|reply_str_value -%}
        {%- if metadataMiaIdeaAUuid == uuids.metadataMiaIdeaYesAUuid -%}
          <p>There are no 'Minimal Metadata About ...' (MIA...) standards for our experiments. However, we have a good idea of what metadata is needed to make it possible for others to read and interpret your data in the future.</p>
        {%- endif -%}
      {%- elif metadataMiaAUuid == uuids.metadataMiaYesAUuid -%}
        {%- set metadataMiaListPath = [metadataMiaPath, uuids.metadataMiaYesAUuid, uuids.metadataMiaListQUuid]|reply_path -%}
        {%- set metadataMiaListItems = repliesMap[metadataMiaListPath]|reply_items -%}
        {%- if metadataMiaListItems|length > 0 -%}
          <p>There are the following 'Minimal Metadata About ...' (MIA...) standards for our experiments:
          <ul>
          {%- for metadataMiaItem in metadataMiaListItems -%}
            {%- set itemPrefix = [metadataMiaListPath, metadataMiaItem]|reply_path -%}
            {%- set metadataMiaWhichPath = [itemPrefix, uuids.metadataMiaWhichQUuid]|reply_path -%}
            {%- set metadataMiaWhichReply = repliesMap[metadataMiaWhichPath] -%}
            <li>{{ macros.integrationValue(metadataMiaWhichReply, uuids.metadataMiaWhichQUuid) }}<li>
          {%- endfor -%}
          </ul>
          </p>
        {%- else -%}
          <p>There are 'Minimal Metadata About ...' (MIA...) standards for our experiments.</p>
        {%- endif -%}
      {%- endif -%}
      {# Metadata #}
      {# Good provenance (lab notebooks) #}
      {%- set provenancePath = [metadataPath, uuids.metadataExpAUuid, uuids.provenanceQUuid]|reply_path -%}
      {%- set provenanceAUuid = repliesMap[provenancePath]|reply_str_value -%}
      {%- if provenanceAUuid == uuids.provenanceLabNotebooksAUuid -%}
        <p>We will use lab notebooks to make sure that there is good provenance of the data analysis.</p>
      {%- elif provenanceAUuid == uuids.provenanceELabNotebookAUuid -%}
        <p>We will use an electronic lab notebook to make sure that there is good provenance of the data analysis.</p>
      {%- elif provenanceAUuid == uuids.provenanceOtherAUuid -%}
        {%- set provenanceOtherPath = [provenancePath, uuids.provenanceOtherAUuid, uuids.provenanceOtherQUuid]|reply_path -%}
        {%- set provenanceOther = repliesMap[provenanceOtherPath]|reply_str_value -%}
        <p>We will use other solution than (electronic) lab notebooks to make sure that there is good provenance of the data analysis{{ ": " ~ provenanceOther|dot if provenanceOther else "." }}
      {%- endif -%}

      {# Metadata standards #}
      {%- set metadataSentences = [] -%}
      {%- set metadataStandardsPath = [metadataPath, uuids.metadataExpAUuid, uuids.metadataStandardsQUuid]|reply_path -%}
      {%- set metadataStandardsAUuid = repliesMap[metadataStandardsPath]|reply_str_value -%}
      {%- if metadataStandardsAUuid == uuids.metadataStandardsExploreAUuid -%}
        {%- set metadataStandardsDCPath = [metadataStandardsPath, uuids.metadataStandardsExploreAUuid, uuids.metadataStandardsDCQUuid]|reply_path -%}
        {%- set metadataStandardsDCAUuid = repliesMap[metadataStandardsDCPath]|reply_str_value -%}
        {%- set metadataStandardsDataCityPath = [metadataStandardsPath, uuids.metadataStandardsExploreAUuid, uuids.metadataStandardsDataCiteQUuid]|reply_path -%}
        {%- set metadataStandardsDataCityAUuid = repliesMap[metadataStandardsDataCityPath]|reply_str_value -%}
        {%- set metadataStandardsDDIPath = [metadataStandardsPath, uuids.metadataStandardsExploreAUuid, uuids.metadataStandardsDDIQUuid]|reply_path -%}
        {%- set metadataStandardsDDIAUuid = repliesMap[metadataStandardsDDIPath]|reply_str_value -%}

        {%- set documentDataW3CPath = [metadataStandardsPath, uuids.metadataStandardsExploreAUuid, uuids.documentDataW3CQUuid]|reply_path -%}
        {%- set documentDataW3CAUuid = repliesMap[documentDataW3CPath]|reply_str_value -%}

        {%- set metadataStandards = [] -%}
        {%- if metadataStandardsDCAUuid == uuids.metadataStandardsDCYesAUuid -%}
          {%- do metadataStandards.append("Dublin Core") -%}
        {%- endif -%}
        {%- if metadataStandardsDCAUuid == uuids.metadataStandardsDataCiteYesAUuid -%}
          {%- do metadataStandards.append("DataCite") -%}
        {%- endif -%}
        {%- if metadataStandardsDCAUuid == uuids.metadataStandardsDCYesAUuid -%}
          {%- do metadataStandards.append("DDI (Data Documentation Initiative)") -%}
        {%- endif -%}
        {%- if metadataStandards|length == 1 -%}
          {%- do metadataSentences.append("We will be documenting the data with " ~ metadataStandards[0] ~ " metadata standard.") -%}
        {%- elif metadataStandards|length == 2 -%}
          {%- do metadataSentences.append("We will be documenting the data with " ~ metadataStandards[0] ~ " and " ~ metadataStandards[1] ~ " metadata standard.") -%}
        {%- elif metadataStandards|length == 3 -%}
          {%- do metadataSentences.append("We will be documenting the data with " ~ metadataStandards[0] ~ ", " ~ metadataStandards[1] ~ ", and " ~ metadataStandards[2] ~ " metadata standard.") -%}
        {%- endif -%}
      {%- endif -%}

      {%- set provenanceW3CPath = [metadataPath, uuids.metadataExpAUuid, uuids.provenanceW3CQUuid]|reply_path -%}
      {%- set provenanceW3CAUuid = repliesMap[provenanceW3CPath]|reply_str_value -%}
      {%- if provenanceW3CAUuid == uuids.provenanceW3CYesAUuid -%}
          {%- do metadataSentences.append("The provenance will be captured using W3C PROV.") -%}
      {%- endif -%}

      {%- if metadataSentences|length > 0 -%}
        <p>{{ metadataSentences|join(" ") }}</p>
      {%- endif -%}

      {# filenames #}
      {%- set metadataNamingPath = [prefix, uuids.metadataNamingQUuid]|reply_path -%}
      {%- set metadataNamingAUuid = repliesMap[metadataNamingPath]|reply_str_value -%}
      {%- if metadataNamingAUuid == uuids.metadataNamingExpAUuid -%}
        {%- set prefix = [metadataNamingPath, uuids.metadataNamingExpAUuid]|reply_path -%}
        {%- set sentences = [] -%}
        {%- set metadataSopPath = [prefix, uuids.metadataSopQUuid]|reply_path -%}
        {%- set metadataSopAUuid = repliesMap[metadataSopPath]|reply_str_value -%}
        {%- if metadataSopAUuid == uuids.metadataSopYesAUuid -%}
          {%- do sentences.append("We made a SOP (Standard Operating Procedure) for file naming.") -%}
          {%- set metadataSopDescribePath = [metadataSopPath, uuids.metadataSopNoAUuid, uuids.metadataSopDescribeQUuid]|reply_path -%}
          {%- set metadataSopDescribe = repliesMap[metadataSopDescribePath]|reply_str_value -%}
          {%- if metadataSopDescribe -%}
            {%- do sentences.append(metadataSopDescribe|capitalize|dot) -%}
          {%- endif -%}
        {%- endif -%}
        {%- set metadataRelationsPath = [prefix, uuids.metadataRelationsQUuid]|reply_path -%}
        {%- set metadataRelationsAUuid = repliesMap[metadataRelationsPath]|reply_str_value -%}
        {%- if metadataRelationsAUuid == uuids.metadataRelationsYesAUuid -%}
          {%- do sentences.append("We will be keeping the relationships between data clear in the file names.") -%}
        {%- endif -%}
        {%- set metadataFilenamesPath = [prefix, uuids.metadataFilenamesQUuid]|reply_path -%}
        {%- set metadataFilenamesAUuid = repliesMap[metadataFilenamesPath]|reply_str_value -%}
        {%- if metadataFilenamesAUuid == uuids.metadataFilenamesYesAUuid -%}
          {%- do sentences.append("All the metadata in the file names also will be available in the proper metadata.") -%}
        {%- endif -%}
        {%- if sentences|length > 0 -%}
          <p>{{ sentences|join(' ') }}</p>
        {%- endif -%}
      {%- endif -%}
    {%- endif -%}
  </div>
</div>
","fileName":"src/questions/02-1-findable.html.j2"},{"uuid":"48643c7a-dd18-4562-a773-12f0c4788dc6","content":"<header>
  <p class="dmp"></p>
  <h1>
    {{ ctx.questionnaireName }}
  </h1>
  <h3>
    Following the <strong>Horizon 2020 DMP Template v2.0</strong>
  </h3>
  <div>
    <dl>
      {%- set contributorsPath = [uuids.adminDetailsCUuid, uuids.contributorsQUuid]|join(".") -%}
      {%- set contributorsItems = repliesMap[contributorsPath]|reply_items -%}
      {%- if contributorsItems|length > 0 -%}
        {%- set contacts = [] -%}
        {%- for i in contributorsItems -%}
          {%- set pathPrefix = [contributorsPath, i]|join(".") ~ "." -%}
          {%- set contributorRole = repliesMap[pathPrefix ~ uuids.contributorRoleQUuid]|reply_str_value -%}
          {%- if contributorRole == uuids.contributorRoleContactPersonAUuid -%}
            {% do contacts.append(pathPrefix)  %}
          {%- endif -%}
        {%- endfor -%}
        {%- set nContacts = contacts|length -%}
        <dt>Contact {{ "people" if (nContacts > 1) else "person" }}</dt>
        <dd>
          {%- if nContacts > 0 -%}
            <ul>
              {%- for pathPrefix in contacts -%}
                <li>
                  {%- set contributorName = repliesMap[pathPrefix ~ uuids.contributorNameQUuid]|reply_str_value -%}
                  {%- set contributorEmail = repliesMap[pathPrefix ~ uuids.contributorEmailQUuid]|reply_str_value -%}
                  {%- set contributorOrcid = repliesMap[pathPrefix ~ uuids.contributorOrcidQUuid]|reply_str_value -%}
                  {%- set contributorAffiliationReply = repliesMap[pathPrefix ~ uuids.contributorAffiliationQUuid] -%}

                  {%- if contributorName -%}
                    <span class="contact-name">{{ contributorName }}</span>
                  {%- else -%}
                    <span class="contact-name missing">(name not given)</span>
                  {%- endif -%}
                  {%- if contributorEmail and contributorOrcid %}
                    ({{ macros.formatEmail(contributorEmail) }}, {{ macros.formatOrcid(contributorOrcid) }})
                  {%- elif contributorEmail %}
                    ({{ macros.formatEmail(contributorEmail) }})
                  {%- elif contributorOrcid %}
                    ({{ macros.formatOrcid(contributorOrcid) }})
                  {%- endif -%}
                  {%- if contributorAffiliationReply %}
                    <br>{{ macros.integrationValue(contributorAffiliationReply, uuids.contributorAffiliationQUuid) }}
                  {%- endif -%}
                </li>
              {%- endfor -%}
            </ul>
          {%- else -%}
            <span class="empty-value">There are no contact people specified yet</span>
          {%- endif -%}
        </dd>
      {%- endif -%}

      <dt>Based on</dt>
      <dd>{{ ctx.package.name }}, {{ ctx.package.version }} (<span class="package-id"><span class="organization-id">{{ ctx.package.organizationId }}</span>:<span class="km-id">{{ ctx.package.kmId }}</span>:<span class="version">{{ ctx.package.version }}</span></span>)</dd>

      {%- if ctx.config.levelsEnabled.levelsEnabled -%}
      <dt>Project phase</dt>
      <dd>{{ macros.renderCurrentLevel() }}</dd>
      {%- endif -%}

      {%- if ctx.createdBy -%}
      <dt>Created by</dt>
      <dd><span class="contact-name">{{ ctx.createdBy.firstName }} {{ ctx.createdBy.lastName }}</span> ({{ macros.formatEmail(ctx.createdBy.email) }}){% if ctx.createdBy.affiliation %}<br>{{ ctx.createdBy.affiliation }}{% endif %}</dd>
      {%- endif -%}

      {% for version in ctx.questionnaireVersions if version.uuid == ctx.questionnaireVersion %}
      <dt>Version</dt>
      <dd>{{version.name}} ({{ version.createdAt|datetime_format("%d %b %Y") }})</dd>
      {% endfor %}

      <dt>Generated on</dt>
      <dd>{{ ctx.createdAt|datetime_format("%d %b %Y") }}</dd>
    </dl>
    <p class="dsw">
      Data Management Plan created in Data Stewardship Wizard &laquo;<a href="https://ds-wizard.org" target="_blank">ds-wizard.org</a>&raquo;
    </p>
  </div>
</header>
","fileName":"src/frontpage.html.j2"},{"uuid":"c43a1b5d-0f92-4e0b-bfc2-60c338a0aed8","content":"{# Used UUID Paths #}
{# Chapter 1 - Administrative details #}
