{%- set refDataConditionsQUuid = "aee03da5-b399-4274-8db4-fac82c6210d3" -%}
{%- set refDataConditionsCC0AUuid = "de5d31e0-dadb-422b-bd88-34f006f11d60" -%}
{%- set refDataConditionsCCBYAUuid = "180bb426-9ebe-4210-b307-eaeb1be12c0e" -%}
{%- set refDataConditionsOtherAUuid = "85dcd5ee-002b-4db7-a0de-241a4b2f0688" -%}
{%- set refDataConditionsOtherQUuid = "dce3e924-717a-4b50-bdac-1b247e9ac18a" -%}
{%- set refDataVersionedQUuid = "10b6548c-21cd-44c8-a8de-47415e7b012e" -%}
