[{"templateId":"dsw:science-europe:1.5.0","uuid":"a2e0f6a8-61c6-4d8f-bae3-fcf0903b899c","content":"{# Formatting macros #}
{%- macro formatEmail(email) -%}
  <span class="contact-email"><a href="mailto:{{ email }}">{{ email }}</a></span>
{%- endmacro -%}

{%- macro formatOrcid(orcid) -%}
  <span class="contact-orcid"><span class="mini-orcid-icon-16"></span> {{ orcid }}</span>
{%- endmacro -%}

{%- macro integrationValue(reply, questionUuid) -%}
  {%- if reply and reply.value.value.type == "IntegrationType" -%}
    {%- set question = km.entities.questions[questionUuid] -%}
    {%- set integration = km.entities.integrations[question.integrationUuid] -%}
    {%- set integrationLink = integration.itemUrl|replace("${id}", reply.value.value.id) -%}
    <a href="{{ integrationLink }}" target="_blank">{{ reply.value.value.value }}</a>
  {%- elif reply %}
    {{ reply.value.value.value }}
  {%- endif -%}
{%- endmacro -%}
","fileName":"parts/macros.html.j2"},{"templateId":"dsw:science-europe:1.5.0","uuid":"b26bd99d-296a-4fe2-a7bd-94fe1b2ed61d","content":"<div id="q-what-data" class="question">
  <h3>1. What data will you collect or create?</h3>
  <div class="answer">

  {%- set measuredPath = [uuids.creatingCUuid, uuids.measuredQUuid]|reply_path -%}
  {%- set measuredAUuid = repliesMap[measuredPath]|reply_str_value -%}
  {%- if measuredAUuid == uuids.measuredYesAUuid -%}
    {%- set measuredDataPath = [measuredPath, uuids.measuredYesAUuid, uuids.measuredDataQUuid]|reply_path -%}
    {%- set measuredDataItems = repliesMap[measuredDataPath]|reply_items -%}
    {# Measured datasets #}
    {%- if measuredDataItems|length > 0 -%}
      <div>
      <h4>Instrument datasets</h4>
      The following instrument datasets will be acquired in the project:
      <ul>
        {%- for datasetItem in measuredDataItems -%}
          {%- set pathPrefix = [measuredDataPath, datasetItem]|reply_path -%}
          <li>
            {%- set measuredDataNamePath = [pathPrefix, uuids.measuredDataNameQUuid]|reply_path -%}
            {%- set measuredDataName = repliesMap[measuredDataNamePath]|reply_str_value -%}
            {%- set measuredDataWhoPath = [pathPrefix, uuids.measuredDataWhoQUuid]|reply_path -%}
            {%- set measuredDataWhoAUuid = repliesMap[measuredDataWhoPath]|reply_str_value -%}
            {%- set measuredDataEquipPath = [pathPrefix, uuids.measuredDataEquipQUuid]|reply_path -%}
            {%- set measuredDataEquipAUuid = repliesMap[measuredDataEquipPath]|reply_str_value -%}
            <strong>{{ measuredDataName if measuredDataName else "(no name given)" }}</strong>
            {# Who will collect #}
            {%- if measuredDataWhoAUuid == uuids.measuredDataWhoExpertsOwnAUuid -%}
              <p>This dataset will be collected by experts in the project, with our own equipment.</p>
            {%- elif measuredDataWhoAUuid == uuids.measuredDataWhoExpertsOutAUuid -%}
              <p>This dataset will be collected by experts in the project, at a specialized infrastructure.</p>
            {%- elif measuredDataWhoAUuid == uuids.measuredDataWhoExternalAUuid -%}
              {%- set mdExternalOwnershipPath = [measuredDataWhoPath, uuids.measuredDataWhoExternalAUuid, uuids.mdExternalOwnershipQUuid]|reply_path -%}
              {%- set mdExternalOwnershipAUuid = repliesMap[mdExternalOwnershipPath]|reply_str_value -%}
              <p>
                This dataset will be collected by an external party.
                {% if mdExternalOwnershipAUuid == uuids.mdExternalOwnershipPartyAUuid -%}
                  The ownership of the resulting data will remain with the external party.
                {%- elif mdExternalOwnershipAUuid == uuids.mdExternalOwnershipPartnersAUuid -%}
                  The project partners acquire full ownership of the data.
                {%- elif mdExternalOwnershipAUuid == uuids.mdExternalOwnershipOtherAUuid -%}
                  {%- set mdExternalOwnershipOtherPath = [mdExternalOwnershipPath, uuids.mdExternalOwnershipOtherAUuid, uuids.mdExternalOwnershipOtherQUuid]|reply_path -%}
                  {%- set mdExternalOwnershipOther = repliesMap[mdExternalOwnershipOtherPath]|reply_str_value -%}
                  {%- if mdExternalOwnershipOther -%}
                    For the ownership of the data we have made the following arrangements: {{ mdExternalOwnershipOther|dot }}
                  {%- endif -%}
                {%- endif -%}
              </p>
            {%- endif -%}
            {# Equipment standard? #}
            {% if measuredDataEquipAUuid == uuids.measuredDataEquipDescribedAUuid -%}
              <p>The equipment is very well described and known.</p>
            {%- elif measuredDataEquipAUuid == uuids.measuredDataEquipCareAUuid -%}
              <p>The equipment is less well described or not completely standard, so we will need to take extra care documenting the process.</p>
            {%- endif -%}
          </li>
        {%- endfor -%}
      </ul>
      </div>
    {%- endif -%}
  {%- endif -%}

  {# Non-equipment datasets #}
  {%- set neqDataPath = [uuids.creatingCUuid, uuids.neqDataQUuid]|reply_path -%}
  {%- set neqDataAUuid = repliesMap[neqDataPath]|reply_str_value -%}
  {%- if neqDataAUuid == uuids.neqDataYesAUuid -%}
    {%- set neqDataPrefix = [neqDataPath, uuids.neqDataYesAUuid]|reply_path -%}
    {%- set neqDataQuestPath = [neqDataPrefix, uuids.neqDataQuestQUuid]|reply_path -%}
    {%- set neqDataQuestAUuid = repliesMap[neqDataQuestPath]|reply_str_value -%}
    {%- set neqDataCaseReportPath = [neqDataPrefix, uuids.neqDataCaseReportQUuid]|reply_path -%}
    {%- set neqDataCaseReportAUuid = repliesMap[neqDataCaseReportPath]|reply_str_value -%}
    {%- set neqDataERecordsPath = [neqDataPrefix, uuids.neqDataERecordsQUuid]|reply_path -%}
    {%- set neqDataERecordsAUuid = repliesMap[neqDataERecordsPath]|reply_str_value -%}
    {%- set neqDataSetsPath = [neqDataPrefix, uuids.neqDataSetsQUuid]|reply_path -%}
    {%- set neqDataSetsItems = repliesMap[neqDataSetsPath]|reply_items -%}

    {%- set questionnaires = neqDataQuestAUuid == uuids.neqDataQuestYesAUuid -%}
    {%- set caseReport = neqDataCaseReportAUuid == uuids.neqDataCaseReportYesAUuid -%}
    {%- set eRecords = neqDataERecordsAUuid == uuids.neqDataERecordsYesAUuid -%}
    {%- if questionnaires or caseReport or eRecords or neqDataSetsItems|length > 0 -%}
      <div>
      <h4>Non-equipment datasets</h4>
      {# Types #}
      {%- set typesNames = [] -%}
      {%- if questionnaires -%}{%- do typesNames.append("questionnaires") -%}{%- endif -%}
      {%- if caseReport -%}{%- do typesNames.append("case report forms") -%}{%- endif -%}
      {%- if eRecords -%}{%- do typesNames.append("electronic patient records") -%}{%- endif -%}
      {%- set typesCount = typesNames|length -%}
      <p>
        {%- if typesCount > 0 -%}
          We also collect data from
          {% if typesCount == 1 -%}
              {{ typesNames[0] }}.
          {%- elif typesCount == 2 -%}
              {{ typesNames[0] }} and {{ typesNames[1] }}.
          {%- else -%}
              {{ typesNames[0] }}, {{ typesNames[1] }}, and {{ typesNames[2] }}.
          {%- endif -%}
        {% endif %}
        {# Datasets #}
        {%- if neqDataSetsItems|length > 0 -%}
          The non-equipment datasets are:
          <ul>
          {%- for neqDataSetItem in neqDataSetsItems -%}
            {%- set pathPrefix = [neqDataSetsPath, neqDataSetItem]|reply_path -%}
            {%- set neqDataSetsName = repliesMap[[pathPrefix, uuids.neqDataSetsNameQUuid]|reply_path]|reply_str_value -%}
            {%- set neqDataSetsDesc = repliesMap[[pathPrefix, uuids.neqDataSetsDescQUuid]|reply_path]|reply_str_value -%}
            <li>
              <strong>{{ neqDataSetsName if neqDataSetsName else "(no name given)" }}</strong>
              {% if neqDataSetsDesc -%}<span class="separator">&ndash;</span> {{ neqDataSetsDesc }}{%- endif -%}
            </li>
          {%- endfor -%}
          </ul>
        {%- endif -%}
      </p>
      </div>
    {%- endif -%}
  {%- endif -%}
  {# Existing datasets #}
  {%- set preexistingPath = [uuids.reusingCUuid, uuids.preexistingQUuid]|reply_path -%}
  {%- set preexistingAUuid = repliesMap[preexistingPath]|reply_str_value -%}
  {%- if preexistingAUuid == uuids.preexistingYesAUuid -%}
    {%- set usingPreexistingPath = [preexistingPath, uuids.preexistingYesAUuid, uuids.usingPreexistingQUuid]|reply_path -%}
    {%- set usingPreexistingAUuid = repliesMap[usingPreexistingPath]|reply_str_value -%}
    {%- if usingPreexistingAUuid == uuids.usingPreexistingYesAUuid -%}
      {%- set refDataPath = [usingPreexistingPath, uuids.usingPreexistingYesAUuid, uuids.refDataQUuid]|reply_path -%}
      {%- set refDataItems = repliesMap[refDataPath]|reply_items -%}
      {%- set nrefDataPath = [usingPreexistingPath, uuids.usingPreexistingYesAUuid, uuids.nrefDataQUuid]|reply_path -%}
      {%- set nrefDataItems = repliesMap[nrefDataPath]|reply_items -%}
      {%- if (refDataItems|length + nrefDataItems|length) > 0 -%}
        <div>
          <h4>Re-used datasets</h4>
          {%- if refDataItems|length > 0 -%}
            <div>We will use the following reference datasets:
            <ul>
              {%- for refDataItem in refDataItems -%}
                {%- set refDataPrefix = [refDataPath, refDataItem]|reply_path -%}
                {%- set refDataNameReply = repliesMap[[refDataPrefix, uuids.refDataNameQUuid]|reply_path] -%}
                {%- set refDataWhere = repliesMap[[refDataPrefix, uuids.refDataWhereQUuid]|reply_path]|reply_str_value -%}
                <li>
                  {# identification #}
                  <strong>{{ macros.integrationValue(refDataNameReply, uuids.refDataNameQUuid) }}</strong>
                  {%- if refDataWhere -%}
                    {%- if refDataWhere.startswith("http://") or refDataWhere.startswith("https://") or refDataWhere.startswith("ftp://") %}
                      (<a href="{{ refDataWhere }}" target="_blank">{{ refDataWhere }}</a>)
                    {%- else %}
                      ({{ refDataWhere }})
                    {%- endif -%}
                  {%- endif -%}
                  {# version #}
                  {%- set refDataVersionedPath = [refDataPrefix, uuids.refDataVersionedQUuid]|reply_path -%}
                  {%- set refDataVersionedAUuid = repliesMap[refDataVersionedPath]|reply_str_value -%}
                  {%- if refDataVersionedAUuid == uuids.refDataVersionedYesAUuid -%}
                    {%- set refDataVersionedWhichPath = [refDataVersionedPath, uuids.refDataVersionedYesAUuid, uuids.refDataVersionedWhichQUuid]|reply_path -%}
                    {%- set refDataVersionedWhich = repliesMap[refDataVersionedWhichPath]|reply_str_value -%}
                    {%- set refDataVersionedChangePath = [refDataVersionedPath, uuids.refDataVersionedYesAUuid, uuids.refDataVersionedChangeQUuid]|reply_path -%}
                    {%- set refDataVersionedChangeAUuid = repliesMap[refDataVersionedChangePath]|reply_str_value -%}
                    {%- if refDataVersionedWhich or refDataVersionedChangeAUuid  %}
                      <p>
                      {% if refDataVersionedWhich -%}
                        We will use version "{{ refDataVersionedWhich }}" of this dataset.
                      {%- endif -%}
                      {%- if refDataVersionedChangeAUuid == uuids.refDataVersionedChangeStayAUuid %}
                        If a new version becomes available during the project, we will stay with the old version.
                      {%- elif refDataVersionedChangeAUuid == uuids.refDataVersionedChangeNewAUuid %}
                        If a new version becomes available during the project, new analyses will be done with the new version.
                      {%- elif refDataVersionedChangeAUuid == uuids.refDataVersionedChangeAllAUuid %}
                        If a new version becomes available during the project, all analyses will be redone with the new version.
                      {%- endif -%}
                      </p>
                    {%- endif -%}
                  {%- endif -%}
                </li>
              {%- endfor -%}
            </ul>
            </div>
          {%- endif -%}
          {%- if nrefDataItems|length > 0 -%}
            <div>We will use the following already existing non-reference datasets:
            <ul>
              {%- for nrefDataItem in nrefDataItems -%}
                {%- set nrefDataNamePath = [nrefDataPath, nrefDataItem, uuids.nrefDataNameQUuid]|reply_path -%}
                {%- set nrefDataNameReply = repliesMap[nrefDataNamePath] -%}
                {%- set nrefDataWhere = repliesMap[[nrefDataPrefix, uuids.nrefDataWhereQUuid]|reply_path]|reply_str_value -%}
                <li>
                  {# identification #}
                  <strong>{{ macros.integrationValue(nrefDataNameReply, uuids.nrefDataNameQUuid) }}</strong>
                  {%- if nrefDataWhere -%}
                    {%- if nrefDataWhere.startswith("http://") or nrefDataWhere.startswith("https://") or nrefDataWhere.startswith("ftp://") %}
                      (<a href="{{ nrefDataWhere }}" target="_blank">{{ nrefDataWhere }}</a>)
                    {%- else %}
                      ({{ nrefDataWhere }})
                    {%- endif -%}
                  {%- endif -%}
                  {# data access #}
                  {%- set nrefDataAccessPath = [nrefDataPath, nrefDataItem, uuids.nrefDataAccessQUuid]|reply_path -%}
                  {%- set nrefDataAccessAUuid = repliesMap[nrefDataAccessPath]|reply_str_value -%}
                  {%- if nrefDataAccessAUuid %}
                    <p>
                    {% if nrefDataAccessAUuid == uuids.nrefDataAccessHaveCopyAUuid -%}
                      We already have a copy of this dataset.
                    {%- elif nrefDataAccessAUuid == uuids.nrefDataAccessWillDownloadAUuid -%}
                      We will download or get a copy.
                    {%- elif nrefDataAccessAUuid == uuids.nrefDataAccessWillOnlineAUuid -%}
                      We will use its online version without downloading it.
                    {%- endif -%}
                    </p>
                  {%- endif -%}
                </li>
              {%- endfor -%}
            </ul>
            </div>
          {%- endif -%}
        </div>
      {%- endif -%}
    {%- endif -%}
  {%- endif -%}
  {# Format/Type with volume #}
  {%- set formatsPath = [uuids.creatingCUuid, uuids.formatsQUuid]|reply_path -%}
  {%- set formatsItems = repliesMap[formatsPath]|reply_items -%}
  {%- if formatsItems|length > 0 -%}
    <h4>Data formats and types</h4>
    <div>We will be using the following data formats and types:
    <ul>
      {%- for formatItem in formatsItems -%}
        {%- set formatPrefix = [formatsPath, formatItem]|reply_path -%}
        {%- set formatNameReply = repliesMap[[formatPrefix, uuids.formatsNameQUuid]|reply_path] -%}
        {%- set formatsIsStandardPath = [formatPrefix, uuids.formatsIsStandardQUuid]|reply_path -%}
        {%- set formatsIsStandardAUuid = repliesMap[formatsIsStandardPath]|reply_str_value -%}
        {%- set formatsIsLTSuitablePath = [formatPrefix, uuids.formatsIsLTSuitableQUuid]|reply_path -%}
        {%- set formatsIsLTSuitableAUuid = repliesMap[formatsIsLTSuitablePath]|reply_str_value -%}
        {%- set formatsVolumePath = [formatPrefix, uuids.formatsVolumeQUuid]|reply_path -%}
        {%- set formatsVolumeAUuid = repliesMap[formatsVolumePath]|reply_str_value -%}
        <li>
          {# identification #}
          <strong>{{ macros.integrationValue(formatNameReply, uuids.formatsNameQUuid) }}</strong>
          {%- if formatsIsStandardAUuid or formatsIsLTSuitableAUuid or formatsVolumeAUuid -%}
            <p>
            {# standard? #}
            {%- if formatsIsStandardAUuid == uuids.formatsIsStandardYesAUuid %}
              It is a standardized format.
            {%- elif formatsIsStandardAUuid == uuids.formatsIsStandardNoAUuid -%}
              {%- set formatsWhyNonStandardPath = [formatsIsStandardPath, uuids.formatsIsStandardNoAUuid, uuids.formatsWhyNonStandardQUuid]|reply_path -%}
              {%- set formatsWhyNonStandardAUuid = repliesMap[formatsWhyNonStandardPath]|reply_str_value -%}
              {%- if formatsWhyNonStandardAUuid == uuids.formatsWhyNSThereIsNoStandardAUuid %}
                It is not a standardized format because there is no standardized format for this data type.
              {%- elif formatsWhyNonStandardAUuid == uuids.formatsWhyNSItIsOptimizedAUuid %}
                It is not a standardized format but it is optimized for processing speed and/or volume.
              {%- elif formatsWhyNonStandardAUuid == uuids.formatsWhyNSAnotherReasonAUuid -%}
                {%- set formatsWhyNSAnotherReasonPath = [formatsWhyNonStandardPath, uuids.formatsWhyNSAnotherReasonAUuid, uuids.formatsWhyNSAnotherReasonQUuid]|reply_path -%}
                {%- set formatsWhyNSAnotherReason = repliesMap[formatsWhyNSAnotherReasonPath]|reply_str_value -%}
                It is not a standardized format.
                {%- if formatsWhyNSAnotherReason %}
                  {{ formatsWhyNSAnotherReason|capitalize|dot }}
                {%- endif -%}
              {%- endif -%}
            {%- endif -%}
            {# long-term suitable? #}
            {%- if formatsIsLTSuitableAUuid == uuids.formatsIsLTSuitableYesAUuid %}
              This is a suitable format for long-term archiving.
            {%- elif formatsIsLTSuitableAUuid == uuids.formatsIsLTSuitableNoAUuid -%}
              {%- set formatsConvertLTSuitablePath = [formatsIsLTSuitablePath, uuids.formatsIsLTSuitableNoAUuid, uuids.formatsConvertLTSuitableQUuid]|reply_path -%}
              {%- set formatsConvertLTSuitableAUuid = repliesMap[formatsConvertLTSuitablePath]|reply_str_value -%}
              {%- if formatsConvertLTSuitableAUuid == uuids.formatsConvertLTSuitableYesAUuid %}
                This is not a suitable format for long-term archiving; however, we plan to convert it to a suitable format before the end of the project.
              {%- elif formatsConvertLTSuitableAUuid == uuids.formatsConvertLTSuitableNoAUuid %}
                We are aware that this is not a suitable format for long-term archiving.
              {%- endif -%}
            {%- endif -%}
            {# volume #}
            {%- if formatsVolumeAUuid == uuids.formatsVolumeSmallAUuid %}
              We will have only a small amount of data stored in this format.
            {%- elif formatsVolumeAUuid == uuids.formatsVolumeTotalAUuid -%}
              {%- set formatsVolumeTotalGB = repliesMap[[formatsVolumePath, uuids.formatsVolumeTotalAUuid, uuids.formatsVolumeTotalGBQUuid]|reply_path]|reply_str_value -%}
              {%- if formatsVolumeTotalGB %}
                We expect to have {{ formatsVolumeTotalGB }} GB of data in this format.
              {%- endif -%}
            {%- elif formatsVolumeAUuid == uuids.formatsVolumeFileSizeAUuid -%}
              {%- set formatsVolumeFiles = repliesMap[[formatsVolumePath, uuids.formatsVolumeFileSizeAUuid, uuids.formatsVolumeFilesQUuid]|reply_path]|reply_str_value -%}
              {%- set formatsVolumeFileGB = repliesMap[[formatsVolumePath, uuids.formatsVolumeFileSizeAUuid, uuids.formatsVolumeFileGBQUuid]|reply_path]|reply_str_value -%}
              {%- if formatsVolumeFiles and formatsVolumeFileGB %}
                We expect to have {{ formatsVolumeFiles }} files of average size {{ formatsVolumeFileGB }} GB (i.e. approximatelly {{ (formatsVolumeFiles|int * formatsVolumeFileGB|float)|round(2) }} GB in total).
              {%- endif -%}
            {%- endif -%}
            </p>
          {%- endif -%}
        </li>
      {%- endfor -%}
    </ul>
    </div>
  {%- endif -%}
  </div>
</div>
","fileName":"parts/questions/01-what-data.html.j2"},{"templateId":"dsw:science-europe:1.5.0","uuid":"fa24e97c-8b7f-496f-bb2a-715d3857c32c","content":"<!DOCTYPE HTML>
{%- set km = ctx.knowledgeModel -%}
{%- set repliesMap = ctx.questionnaireReplies -%}
{%- set report = ctx.report -%}
{% import "parts/macros.html.j2" as macros with context %}
{% import "parts/uuids.j2" as uuids with context %}
<html lang="en">
  <head>
    <title>Data Management Plan</title>
    <meta charset="utf-8">
    <style>
      {% include "parts/style.css" %}
    </style>
  </head>
  <body>
    <article class="dmp">
      {% include "parts/header.html.j2" with context %}
      {% include "parts/projects.html.j2" with context %}
      {% include "parts/content.html.j2" with context %}
    </article>
  </body>
</html>
","fileName":"parts/index.html.j2"},{"templateId":"dsw:science-europe:1.5.0","uuid":"8f916a69-3567-4c82-b6f7-5744b1c1c15d","content":"<div id="q-access-security" class="question">
  <h3>7. How will you manage access and security?</h3>

  <div class="answer">
    {%- set risksPathPrefix = [uuids.processingCUuid, uuids.risksQUuid, uuids.risksExploreAUuid]|reply_path -%}
    {%- set sharedWorkspacePrefix = [uuids.processingCUuid, uuids.sharedWorkspaceQUuid, uuids.sharedWorkspaceYesAUuid]|reply_path -%}
    {# Basic risks #}
    {%- set risksStoreLabPath = [risksPathPrefix, uuids.risksStoreLabQUuid]|reply_path -%}
    {%- set risksStoreLabAUuid = repliesMap[risksStoreLabPath]|reply_str_value -%}
    {%- set risksCarryHomePath = [risksPathPrefix, uuids.risksCarryHomeQUuid]|reply_path -%}
    {%- set risksCarryHomeAUuid = repliesMap[risksCarryHomePath]|reply_str_value -%}
    {%- set risksCarryHomeEncPath = [risksCarryHomePath, uuids.risksCarryHomeYesAUuid, uuids.risksCarryHomeEncQUuid]|reply_path -%}
    {%- set risksCarryHomeEncAUuid = repliesMap[risksCarryHomeEncPath]|reply_str_value -%}
    {%- set risksCarryHomePassPath = [risksCarryHomePath, uuids.risksCarryHomeYesAUuid, uuids.risksCarryHomePassQUuid]|reply_path -%}
    {%- set risksCarryHomePassAUuid = repliesMap[risksCarryHomePassPath]|reply_str_value -%}
    {%- set risksCertificationsPath = [risksPathPrefix, uuids.risksCertificationsQUuid]|reply_path -%}
    {%- set risksCertificationsAUuid = repliesMap[risksCertificationsPath]|reply_str_value -%}
    {%- set risksHttpsPath = [risksPathPrefix, uuids.risksHttpsQUuid]|reply_path -%}
    {%- set risksHttpsAUuid = repliesMap[risksHttpsPath]|reply_str_value -%}
    {%- set risksInstructedPath = [risksPathPrefix, uuids.risksInstructedQUuid]|reply_path -%}
    {%- set risksInstructedAUuid = repliesMap[risksInstructedPath]|reply_str_value -%}

    {%- set homeEncrypted = risksCarryHomeEncAUuid == uuids.risksCarryHomeEncYesAUuid -%}
    {%- set homePassword = risksCarryHomePassAUuid == uuids.risksCarryHomePassYesAUuid -%}
    {%- set storeLab = risksStoreLabAUuid == uuids.risksStoreLabNoAUuid -%}

    {%- if storeLab or (risksCarryHomeAUuid == uuids.risksCarryHomeNoAUuid) or (risksCarryHomeAUuid == uuids.risksCarryHomeYesAUuid and (homeEncrypted or homePassword)) or (risksCertificationsAUuid == uuids.risksCertificationsYesAUuid) or (risksHttpsAUuid == uuids.risksHttpsYesAUuid) or (risksInstructedAUuid == uuids.risksInstructedYesAUuid) %}
      <p>
        {%- if storeLab %}
          Project members will not store data or software on computers in the lab or external hard drives connected to those computers.
        {%- endif -%}
        {%- if risksCarryHomeAUuid == uuids.risksCarryHomeNoAUuid %}
          {%- if storeLab -%}They{%- else -%}Project members{%- endif %} will not carry data with them (e.g. on laptops, USB sticks, or other external media).
        {%- elif homeEncrypted and homePassword %}
          {%- if storeLab -%}They{%- else -%}Project members{%- endif %} can carry data with them on encrypted data carriers and password-protected laptops.
        {%- elif homeEncrypted %}
          {%- if storeLab -%}They{%- else -%}Project members{%- endif %} can carry data with them on encrypted data carriers.
        {%- elif homePassword %}
          {%- if storeLab -%}They{%- else -%}Project members{%- endif %} can carry data with them on password-protected laptops.
        {%- endif -%}
        {%- if risksCertificationsAUuid == uuids.risksCertificationsYesAUuid %}
          All data centers where project data is stored carry sufficient certifications.
        {%- endif -%}
        {%- if risksHttpsAUuid == uuids.risksHttpsYesAUuid %}
          All project web services addressed via secure http (https://...).
        {%- endif -%}
        {%- if risksInstructedAUuid == uuids.risksInstructedYesAUuid %}
          Project members have been instructed about both generic and specific risks to the project.
        {%- endif -%}
      </p>
    {%- endif -%}
    {# Information loss, leak, and vandalism #}
    {%- set risksInfoLossPath = [risksPathPrefix, uuids.risksInfoLossQUuid]|reply_path -%}
    {%- set risksInfoLossAUuid = repliesMap[risksInfoLossPath]|reply_str_value -%}
    {%- set risksInfoLeakPath = [risksPathPrefix, uuids.risksInfoLeakQUuid]|reply_path -%}
    {%- set risksInfoLeakAUuid = repliesMap[risksInfoLeakPath]|reply_str_value -%}
    {%- set risksInfoVandalismPath = [risksPathPrefix, uuids.risksInfoVandalismQUuid]|reply_path -%}
    {%- set risksInfoVandalismAUuid = repliesMap[risksInfoVandalismPath]|reply_str_value -%}
    {%- set risksInfoLossTexts = {
      uuids.risksInfoLossSmallAUuid: "The possible impact to the project or organization if information is lost is small.",
      uuids.risksInfoLossLowAUuid: "The risk of information loss in the project or organization is acceptably low.",
      uuids.risksInfoLossWillAUuid: "We will mitigate information loss risk for the project or organization."
    } -%}
    {%- set risksInfoLeakTexts = {
      uuids.risksInfoLeakSmallAUuid: "The possible impact to the project or organization if information is leaked is small.",
      uuids.risksInfoLeakLowAUuid: "The risk of information leak in the project or organization is acceptably low.",
      uuids.risksInfoLeakWillAUuid: "We will mitigate information leak risk for the project or organization."
    } -%}
    {%- set risksInfoVandalismTexts = {
      uuids.risksInfoVandalismSmallAUuid: "The possible impact to the project or organization if information is vandalised is small.",
      uuids.risksInfoVandalismLowAUuid: "The risk of information vandalism in the project or organization is acceptably low.",
      uuids.risksInfoVandalismWillAUuid: "We will mitigate information vandalism risk for the project or organization."
    } -%}
    {% set risksInfoXText = [
      risksInfoLossTexts[risksInfoLossAUuid],
      risksInfoLeakTexts[risksInfoLeakAUuid],
      risksInfoVandalismTexts[risksInfoVandalismAUuid]
    ]|join(" ") %}
    {%- if risksInfoXText|length > 0 -%}
      <p>{{ risksInfoXText }}</p>
    {%- endif -%}
    {# Personal data #}
    {%- set risksPersonalDataPath = [risksPathPrefix, uuids.risksPersonalDataQUuid]|reply_path -%}
    {%- set risksPersonalDataAUuid = repliesMap[risksPersonalDataPath]|reply_str_value -%}
    {%- if risksPersonalDataAUuid == uuids.risksPersonalDataNotUsingAUuid -%}
      <p>We are not using any personal information.</p>
    {%- elif risksPersonalDataAUuid == uuids.risksPersonalDataPseudoAUuid -%}
      <p>
        All personal information will be processed in pseudonymized form only.
        {%- set risksPseudonymizationPath = [risksPersonalDataPath, uuids.risksPersonalDataPseudoAUuid, uuids.risksPseudonymizationQUuid]|reply_path -%}
        {%- set risksPseudonymizationAUuid = repliesMap[risksPseudonymizationPath]|reply_str_value -%}
        {%- if risksPseudonymizationAUuid == uuids.risksPseudonymizationInsideAUuid %}
          We pseudonymize inside the project, only limited people can access the keys.
        {%- elif risksPseudonymizationAUuid == uuids.risksPseudonymizationPartyAUuid %}
          Pseudonymization is handled by an independent party in order to allow data coupling.
        {%- elif risksPseudonymizationAUuid == uuids.risksPseudonymizationAnotherAUuid -%}
          {%- set risksPseudonymizationAnotherQUuid = [risksPseudonymizationPath, uuids.risksPseudonymizationAnotherAUuid, uuids.risksPseudonymizationAnotherQUuid]|reply_path -%}
          {%- set risksPseudonymizationAnother = repliesMap[risksPseudonymizationAnotherQUuid]|reply_str_value %}
          We have a specific way of pseudonymization{{ ": " ~ risksPseudonymizationAnother|dot if risksPseudonymizationAnother else "." }}
        {%- endif -%}
      </p>
    {%- elif risksPersonalDataAUuid == uuids.risksPersonalDataAnonymousAUuid -%}
      <p>All personal data will be collected anonymously.</p>
    {%- elif risksPersonalDataAUuid == uuids.risksPersonalDataAnonymizedAUuid -%}
      <p>All personal data will be anonymized as early as possible.</p>
    {%- endif -%}
    {# Access control in shared workspace #}
    {%- set sharedAccessControlPath = [sharedWorkspacePrefix, uuids.sharedAccessControlQUuid]|reply_path -%}
    {%- set sharedAccessControlAUuid = repliesMap[sharedAccessControlPath]|reply_str_value -%}
    {%- if sharedAccessControlAUuid == uuids.sharedAccessControlMembersRWAUuid -%}
      <p>Only all project members have read/write access to the data.</p>
    {%- elif sharedAccessControlAUuid == uuids.sharedAccessControlInstituteRAUuid -%}
      <p>Anyone in the institute has read access to the data; all project members have read/write access.</p>
    {%- elif sharedAccessControlAUuid == uuids.sharedAccessControlMembersRAUuid -%}
      <p>Only project members will have read access; only selected project members will be able to write data.</p>
    {%- elif sharedAccessControlAUuid == uuids.sharedAccessControlMembersRequestAUuid -%}
      <p>Anyone in the institute can read data, only project members can request write access.</p>
    {%- endif -%}
  </div>
</div>
","fileName":"parts/questions/07-access-security.html.j2"},{"templateId":"dsw:science-europe:1.5.0","uuid":"345f01e9-7409-4cc6-a20c-f86d7385a21b","content":"<div id="q-how-share" class="question">
  <h3>10. How will you share the data?</h3>

  <div class="answer">
    {# Produced datasets #}
    {%- set producedDataPath = [uuids.preservingCUuid, uuids.producedDataQUuid]|reply_path -%}
    {%- set producedDataItems = repliesMap[producedDataPath]|reply_items -%}
    {%- if producedDataItems|length > 0 -%}
      <ul>
      {%- for i in producedDataItems -%}
        {%- set pathPrefix = [producedDataPath, i]|reply_path -%}
        {%- set producedDataName = repliesMap[[pathPrefix, uuids.producedDataNameQUuid]|reply_path]|reply_str_value -%}
        {# Only published #}
        {%- set isPublishedDataPath = [pathPrefix, uuids.isPublishedDataQUuid]|reply_path -%}
        {%- set isPublishedDataAUuid = repliesMap[isPublishedDataPath]|reply_str_value -%}
        {% if isPublishedDataAUuid == uuids.isPublishedDataYesAUuid %}
          <li>
            <strong>{{ producedDataName if producedDataName else "(no name given)" }}</strong>
            {# Identifiers #}
            {% set identifiersPath = [pathPrefix, uuids.producedDataIdentifiersQUuid]|reply_path -%}
            {%- set identifiersItems = repliesMap[identifiersPath]|reply_items -%}
            {%- if identifiersItems|length > 0 -%}
            <br>The dataset has the following identifiers:
            <ul>
            {%- for j in identifiersItems -%}
              {%- set identifierPrefix = [identifiersPath, j]|reply_path -%}
              {%- set identifierTypePath = [identifierPrefix, uuids.dataIdentifierTypeQUuid]|reply_path -%}
              {%- set identifierTypeAUuid = repliesMap[identifierTypePath]|reply_str_value -%}
              {%- set identifierValuePath = [identifierPrefix, uuids.dataIdentifierIdenfitierQUuid]|reply_path -%}
              {%- set identifierValue = repliesMap[identifierValuePath]|reply_str_value -%}
              {%- if identifierTypeAUuid == uuids.dataIdentifierTypeHandleAUuid -%}
                <li>Handle: <strong>{{ identifierValue }}</strong></li>
              {%- elif identifierTypeAUuid == uuids.dataIdentifierTypeDoiAUuid -%}
                <li>DOI: <strong><a href="https://doi.org/{{ identifierValue }}">{{ identifierValue }}</a></strong></li>
              {%- elif identifierTypeAUuid == uuids.dataIdentifierTypeArkAUuid -%}
                <li>ARK: <strong>{{ identifierValue }}</strong></li>
              {%- elif identifierTypeAUuid == uuids.dataIdentifierTypeUrlAUuid -%}
                <li>URL: <strong><a href="{{ identifierValue }}">{{ identifierValue }}</a></strong></li>
              {%- elif identifierTypeAUuid == uuids.dataIdentifierTypeOtherAUuid -%}
                {%- set identifierType = repliesMap[[identifierTypePath, uuids.dataIdentifierTypeOtherAUuid, uuids.dataIdentifierTypeOtherTypeQUuid]|reply_path]|reply_str_value -%}
                <li>{{ identifierType }}: <strong>{{ identifierValue }}</strong></li>
              {%- endif -%}
            {%- endfor -%}
            </ul>
            {%- endif %}
            {# Distributions #}
            {% set distrosPath = [isPublishedDataPath, uuids.isPublishedDataYesAUuid, uuids.publishedDistrosQUuid]|reply_path -%}
            {%- set distrosItems = repliesMap[distrosPath]|reply_items -%}
            {%- if distrosItems|length > 0 -%}
              <br>The distributions will be available as follows:
              <ul>
              {%- for j in distrosItems -%}
                {%- set distroPrefix = [distrosPath, j]|reply_path -%}
                {%- set distroRepositoryKindPath = [distroPrefix, uuids.publishedDataRepositoryKindQUuid]|reply_path -%}
                {%- set distroRepositoryKindAUuid = repliesMap[distroRepositoryKindPath]|reply_str_value -%}
                {%- set distroSharePath = [distroPrefix, uuids.publishedDistroShareQUuid]|reply_path -%}
                {%- set distroShareAUuid = repliesMap[distroSharePath]|reply_str_value -%}
                {%- if distroRepositoryKindAUuid and distroShareAUuid -%}
                  <li>
                  {%- if distroShareAUuid == uuids.publishedDistroShareOpenAUuid %}
                    Open (shared with anyone)
                  {%- elif distroShareAUuid == uuids.publishedDistroShareSharedAUuid %}
                    Shared with a predefined list of people
                  {%- elif distroShareAUuid == uuids.publishedDistroShareClosedAUuid %}
                    Closed (not meant for sharing)
                  {%- endif %}
                  {% if distroRepositoryKindAUuid == uuids.publishedDataRepositoryDomainSpecificAUuid %}
                    using a domain-specific repository
                    {%- set domainSpecificRepoNamePath = [distroRepositoryKindPath, uuids.publishedDataRepositoryDomainSpecificAUuid, uuids.domainSpecificRepoNameQUuid]|reply_path -%}
                    {%- set domainSpecificRepoNameReply = repliesMap[domainSpecificRepoNamePath] -%}
                    {%- if domainSpecificRepoNameReply -%}
                      : {{ macros.integrationValue(domainSpecificRepoNameReply, uuids.domainSpecificRepoNameQUuid) }}.
                    {%- else -%}.
                    {% endif -%}
                  {%- elif distroRepositoryKindAUuid == uuids.publishedDataRepositoryNationalAUuid %}
                    using our national repository.
                  {%- elif distroRepositoryKindAUuid == uuids.publishedDataRepositoryInstitutionalAUuid %}
                    using our institutional repository.
                  {%- elif distroRepositoryKindAUuid == uuids.publishedDataRepositorySpecialAUuid %}
                    using a special-purpose repository for the project.
                  {%- endif -%}
                  {# Licenses #}
                  {%- set licensesPath = [distroPrefix, uuids.publishedDataLicensesQUuid]|reply_path -%}
                  {%- set licensesItems = repliesMap[licensesPath]|reply_items -%}
                  {%- if licensesItems|length > 0 %}
                    The distribution will be available under the following {{ "licenses" if licensesItems|length > 1 else "license" }}:
                    <ul>
                    {%- for k in licensesItems -%}
                      <li>
                      {%- set licensePrefix = [licensesPath, k]|reply_path -%}
                      {%- set licenseStartPath = [licensePrefix, uuids.publishedDataLicenseStartQUuid]|reply_path -%}
                      {%- set licenseStart = repliesMap[licenseStartPath]|reply_str_value -%}
                      {% if licenseStart %}
                        Starting {{ licenseStart }}: 
                      {% endif %}

                      {%- set publishedDataLicensePath = [licensePrefix, uuids.publishedDataLicenseQUuid]|reply_path -%}
                      {%- set publishedDataLicenseAUuid = repliesMap[publishedDataLicensePath]|reply_str_value -%}
                      {%- if publishedDataLicenseAUuid == uuids.publishedDataLicenseCC0AUuid %}
                        Freely available for any use (public domain or CC0).
                      {%- elif publishedDataLicenseAUuid == uuids.publishedDataLicenseCCBYAUuid %}
                        Freely available with obligation to quote the source (e.g. CC-BY).
                      {%- elif publishedDataLicenseAUuid == uuids.publishedDataLicenseRestrictAUuid -%}
                        {%- set licenseRestrictConditionsPath = [publishedDataLicensePath, uuids.publishedDataLicenseRestrictAUuid, uuids.licenseRestrictConditionsQUuid]|reply_path -%}
                        {%- set licenseRestrictConditions = repliesMap[licenseRestrictConditionsPath]|reply_str_value -%}
                        {%- set licenseRestrictLinkPath = [publishedDataLicensePath, uuids.publishedDataLicenseRestrictAUuid, uuids.licenseRestrictLinkQUuid]|reply_path -%}
                        {%- set licenseRestrictLink = repliesMap[licenseRestrictLinkPath]|reply_str_value -%}
                        {%- set licenseRestrictAccessPath = [publishedDataLicensePath, uuids.publishedDataLicenseRestrictAUuid, uuids.licenseRestrictAccessQUuid]|reply_path -%}
                        {%- set licenseRestrictAccessAUuid = repliesMap[licenseRestrictAccessPath]|reply_str_value -%}
                        {%- set licenseRestrictMetadataPath = [publishedDataLicensePath, uuids.publishedDataLicenseRestrictAUuid, uuids.licenseRestrictMetadataQUuid]|reply_path -%}
                        {%- set licenseRestrictMetadataAUuid = repliesMap[licenseRestrictMetadataPath]|reply_str_value %}

                        Available under some restrictions, which we will follow in our project{{ ": " ~ licenseRestrictConditions|dot if licenseRestrictConditions else "." }}
                        {%- if licenseRestrictAccessAUuid == uuids.licenseRestrictAccessRequestAUuid %}
                          Re-users will be able to get access by request to the contact person.
                        {%- elif licenseRestrictAccessAUuid == uuids.licenseRestrictAccessCommitteeAUuid %}
                          Re-users will be able to get access through a <em>Data Access Committee</em> for the project.
                        {%- elif licenseRestrictAccessAUuid == uuids.licenseRestrictAccessAnotherAUuid -%}
                          {%- set licenseRestrictAccessAnotherPath = [licenseRestrictAccessPath, uuids.licenseRestrictAccessAnotherAUuid, uuids.licenseRestrictAccessAnotherQUuid]|reply_path -%}
                          {%- set licenseRestrictAccessAnother = repliesMap[licenseRestrictAccessAnotherPath]|reply_str_value %}
                          Re-users will be able to get access through a specialized process{{ ": " ~ licenseRestrictAccessAnother|dot if licenseRestrictAccessAnother else "." }}
                        {%- endif -%}
                        {%- if licenseRestrictMetadataAUuid == uuids.licenseRestrictMetadataYesAUuid %}
                          The conditions will be published as part of open metadata.
                        {%- endif -%}
                        {% if licenseRestrictLink %}
                          More infomation about the restrictions can be found here: <a href="{{ licenseRestrictLink }}">{{ licenseRestrictLink }}</a>.
                        {% endif %}
                      {%- endif -%}
                      </li>
                    {%- endfor -%}
                    </ul>
                  {%- endif -%}
                  </li>
                {%- endif -%}
              {%- endfor -%}
              </ul>
            {%- endif -%}
            {# Catalogue #}
            {%- set publishedDataCataloguePath = [isPublishedDataPath, uuids.isPublishedDataYesAUuid, uuids.publishedDataCatalogueQUuid]|reply_path -%}
            {%- set publishedDataCatalogueAUuid = repliesMap[publishedDataCataloguePath]|reply_str_value -%}
            {%- if publishedDataCatalogueAUuid == uuids.publishedDataCatalogueYesAUuid -%}
              <br>We will be adding a reference to the published data to at least one data catalogue.
            {%- endif -%}
          </li>
        {%- endif -%}
      {%- endfor -%}
      </ul>
    {%- endif -%}

    <p>Information about used repositories (i.e. where will potential users find out about the data) is provided in Section E, Question 9.</p>

    <p>Embargo on the data is described in Section C, Question 5, and Section F, Question 11.</p>
  </div>
</div>
","fileName":"parts/questions/10-how-share.html.j2"},{"templateId":"dsw:science-europe:1.5.0","uuid":"a8a35dce-e695-401d-b4b3-896b64eb1a81","content":"<div id="q-which-longterm" class="question">
  <h3>8. Which data are of long-term value and should be retained, shared, and/or preserved?</h3>

  <div class="answer">
    {# Produced datasets #}
    {%- set producedDataPath = [uuids.preservingCUuid, uuids.producedDataQUuid]|reply_path -%}
    {%- set producedDataItems = repliesMap[producedDataPath]|reply_items -%}
    {%- if producedDataItems|length > 0 -%}
      <p>
      We plan to produce the following datasets:
      <ul>
      {%- for i in producedDataItems -%}
        {%- set pathPrefix = [producedDataPath, i]|reply_path -%}
        {%- set producedDataName = repliesMap[[pathPrefix, uuids.producedDataNameQUuid]|reply_path]|reply_str_value -%}
        <li>
          <strong>{{ producedDataName if producedDataName else "(no name given)" }}</strong>
          {# If will be published #}
          {%- set isPublishedDataPath = [pathPrefix, uuids.isPublishedDataQUuid]|reply_path -%}
          {%- set isPublishedDataAUuid = repliesMap[isPublishedDataPath]|reply_str_value -%}
          {% if isPublishedDataAUuid == uuids.isPublishedDataNoAUuid %}
            (not published)
          {% elif isPublishedDataAUuid == uuids.isPublishedDataYesAUuid %}
            (published)
          {% endif -%}
          {# How long will be stored + Metadata FAIRness #}
          {%- set publishedDataHowLongPath = [pathPrefix, uuids.publishedDataHowLongQUuid]|reply_path -%}
          {%- set publishedDataHowLongAUuid = repliesMap[publishedDataHowLongPath]|reply_str_value -%}
          {%- set publishedDataMetadataPersistentPath = [pathPrefix, uuids.publishedDataMetadataPersistentQUuid]|reply_path -%}
          {%- set publishedDataMetadataPersistentAUuid = repliesMap[publishedDataMetadataPersistentPath]|reply_str_value -%}
          {%- if publishedDataHowLongAUuid or (publishedDataMetadataPersistentAUuid == uuids.publishedDataMetadataPersistentYesAUuid) %}
            {% if publishedDataHowLongAUuid == uuids.publishedDataHowLongTechnicalAUuid -%}
              <span class="separator">&ndash;</span> This data set will be kept available as long as technically possible.
            {%- elif publishedDataHowLongAUuid == uuids.publishedDataHowLongLegalAUuid -%}
              <span class="separator">&ndash;</span> This data set will be kept available until it needs to be deleted for legal reasons.
            {%- elif publishedDataHowLongAUuid == uuids.publishedDataHowLongFixedAUuid -%}
              {%- set publishedDataHowLongFixedPath = [publishedDataHowLongPath, uuids.publishedDataHowLongFixedAUuid, uuids.publishedDataHowLongFixedQUuid]|reply_path -%}
              {%- set publishedDataHowLongFixed = repliesMap[publishedDataHowLongFixedPath]|reply_str_value -%}
              <span class="separator">&ndash;</span> This data set will be kept available for a fixed period (prepaid){{ " of: " ~ publishedDataHowLongFixed|dot if publishedDataHowLongFixed else "." }}
            {%- endif -%}
            {%- if publishedDataMetadataPersistentAUuid == uuids.publishedDataMetadataPersistentYesAUuid %}
              <span class="separator">&ndash;</span> The metadata will be available even when the data no longer exists.
            {%- endif -%}
          {%- endif -%}
        </li>
      {%- endfor -%}
      </ul>
      </p>
    {%- endif -%}
  </div>
</div>
","fileName":"parts/questions/08-which-longterm.html.j2"},{"templateId":"dsw:science-europe:1.5.0","uuid":"5c3fe2c2-1dba-43ac-9e71-6ef8c9e71296","content":"<div id="q-restrictions" class="question">
  <h3>11. Are any restrictions on data sharing required?</h3>

  <div class="answer">
    <p>Ethical and legal restrictions are documented under Section C. We have used the Data Stewardship Wizard, which made us aware of options to minimize the restrictions.</p>

    {%- set openImmediatelyPath = [uuids.accessCUuid, uuids.openImmediatelyQUuid]|reply_path -%}
    {%- set openImmediatelyAUuid = repliesMap[openImmediatelyPath]|reply_str_value -%}
    {%- if openImmediatelyAUuid == uuids.openImmediatelyYesAUuid -%}
      <p>No data sharing agreement will be required.</p>
    {%- elif openImmediatelyAUuid == uuids.openImmediatelyNoAUuid -%}
      {%- set pathPrefix = [openImmediatelyPath, uuids.openImmediatelyNoAUuid]|reply_path -%}
      {%- set notOpenLimitedEmbargoPath = [pathPrefix, uuids.notOpenLimitedEmbargoQUuid]|reply_path -%}
      {%- set notOpenLimitedEmbargoAUuid = repliesMap[notOpenLimitedEmbargoPath]|reply_str_value -%}
      {%- if notOpenLimitedEmbargoAUuid == uuids.notOpenLimitedEmbargoYesAUuid -%}
        {%- set notOpenLimitedEmbargoPeriodPath = [notOpenLimitedEmbargoPath, uuids.notOpenLimitedEmbargoYesAUuid, uuids.notOpenLimitedEmbargoPeriodQUuid]|reply_path -%}
        {%- set notOpenLimitedEmbargoPeriod = repliesMap[notOpenLimitedEmbargoPeriodPath]|reply_str_value -%}
        <p>
          Data cannot be completely open due to legal reasons. But data that is not legally restrained will be released after a fixed time period.
          {%- if notOpenLimitedEmbargoPeriod %}
            The embargo period will be {{ notOpenLimitedEmbargoPeriod|dot }}
          {%- endif %}
        </p>
      {%- endif -%}

      {%- set legalReasonsAuthenticatedPath = [pathPrefix, uuids.notOpenLegalReasonsQUuid, uuids.notOpenLegalReasonsYesAUuid, uuids.legalReasonsAuthenticatedQUuid]|reply_path -%}
      {%- set legalReasonsAuthenticatedAUuid = repliesMap[legalReasonsAuthenticatedPath]|reply_str_value -%}
      {%- if legalReasonsAuthenticatedAUuid == uuids.legalReasonsAuthenticatedYesAUuid -%}
        <p>
          A data sharing agreement will be required.
          {%- set legalReasonsAuthorizePath = [legalReasonsAuthenticatedPath, uuids.legalReasonsAuthenticatedYesAUuid, uuids.legalReasonsAuthorizeQUuid]|reply_path -%}
          {%- set legalReasonsAuthorizeAUuid = repliesMap[legalReasonsAuthorizePath]|reply_str_value -%}
          {%- if legalReasonsAuthorizeAUuid == uuids.legalReasonsAuthorizeMemberAUuid %}
            People can apply to one of the project members.
          {%- elif legalReasonsAuthorizeAUuid == uuids.legalReasonsAuthorizeNewCommitteeAUuid %}
            People can apply to the data access committee that we will set up.
          {%- elif legalReasonsAuthorizeAUuid == uuids.legalReasonsAuthorizeOldCommitteeAUuid %}
            {%- set legalReasonsAuthorizeOldCommitteePath = [legalReasonsAuthorizePath, uuids.legalReasonsAuthorizeOldCommitteeAUuid, uuids.legalReasonsAuthorizeOldCommitteeQUuid]|reply_path -%}
            {%- set legalReasonsAuthorizeOldCommittee = repliesMap[legalReasonsAuthorizeOldCommitteePath]|reply_str_value -%}
            {%- if legalReasonsAuthorizeOldCommittee %}
              People can apply to the <em>{{ legalReasonsAuthorizeOldCommittee }}</em> data access committee.
            {%- else -%}
              People can apply to an existing data access committee.
            {%- endif -%}
          {%- elif legalReasonsAuthorizeAUuid == uuids.legalReasonsAuthorizeOtherAUuid %}
            {%- set legalReasonsAuthorizeOtherPath = [legalReasonsAuthorizePath, uuids.legalReasonsAuthorizeOtherAUuid, uuids.legalReasonsAuthorizeOtherQUuid]|reply_path -%}
            {%- set legalReasonsAuthorizeOther = repliesMap[legalReasonsAuthorizeOtherPath]|reply_str_value %}
            For the authorization of potential users, we will make special arrangements{{ ": " ~ legalReasonsAuthorizeOther|dot if legalReasonsAuthorizeOther else "." }}
          {%- endif -%}
        </p>
      {%- endif -%}
    {%- endif -%}
  </div>
</div>
","fileName":"parts/questions/11-restrictions.html.j2"},{"templateId":"dsw:science-europe:1.5.0","uuid":"405abf8d-3cfd-4518-9afe-eaba0de49fec","content":"<header>
  <p class="dmp"></p>
  <h1>
    {{ ctx.questionnaireName }}
  </h1>
  <div>
    <dl>
      {%- set contributorsPath = [uuids.adminDetailsCUuid, uuids.contributorsQUuid]|join(".") -%}
      {%- set contributorsItems = repliesMap[contributorsPath]|reply_items -%}
      {%- if contributorsItems|length > 0 -%}
        {%- set contacts = [] -%}
        {%- for i in contributorsItems -%}
          {%- set pathPrefix = [contributorsPath, i]|join(".") ~ "." -%}
          {%- set contributorRole = repliesMap[pathPrefix ~ uuids.contributorRoleQUuid]|reply_str_value -%}
          {%- if contributorRole == uuids.contributorRoleContactPersonAUuid -%}
            {% do contacts.append(pathPrefix)  %}
          {%- endif -%}
        {%- endfor -%}
        {%- set nContacts = contacts|length -%}
        <dt>Contact {{ "people" if (nContacts > 1) else "person" }}</dt>
        <dd>
          {%- if nContacts > 0 -%}
            <ul>
              {%- for pathPrefix in contacts -%}
                <li>
                  {%- set contributorName = repliesMap[pathPrefix ~ uuids.contributorNameQUuid]|reply_str_value -%}
                  {%- set contributorEmail = repliesMap[pathPrefix ~ uuids.contributorEmailQUuid]|reply_str_value -%}
                  {%- set contributorOrcid = repliesMap[pathPrefix ~ uuids.contributorOrcidQUuid]|reply_str_value -%}
                  {%- set contributorAffiliationReply = repliesMap[pathPrefix ~ uuids.contributorAffiliationQUuid] -%}

                  {%- if contributorName -%}
                    <span class="contact-name">{{ contributorName }}</span>
                  {%- else -%}
                    <span class="contact-name missing">(name not given)</span>
                  {%- endif -%}
                  {%- if contributorEmail and contributorOrcid %}
                    ({{ macros.formatEmail(contributorEmail) }}, {{ macros.formatOrcid(contributorOrcid) }})
                  {%- elif contributorEmail %}
                    ({{ macros.formatEmail(contributorEmail) }})
                  {%- elif contributorOrcid %}
                    ({{ macros.formatOrcid(contributorOrcid) }})
                  {%- endif -%}
                  {%- if contributorAffiliationReply %}
                    <br>{{ macros.integrationValue(contributorAffiliationReply, uuids.contributorAffiliationQUuid) }}
                  {%- endif -%}
                </li>
              {%- endfor -%}
            </ul>
          {%- else -%}
            <span class="empty-value">There are no contact people specified yet</span>
          {%- endif -%}
        </dd>
      {%- endif -%}

      <dt>Based on</dt>
      <dd>{{ ctx.package.name }}, {{ ctx.package.version }} (<span class="package-id"><span class="organization-id">{{ ctx.package.organizationId }}</span>:<span class="km-id">{{ ctx.package.kmId }}</span>:<span class="version">{{ ctx.package.version }}</span></span>)</dd>

      {%- if ctx.phaseUuid -%}
      <dt>Project phase</dt>
      <dd>{{ km.entities.phases[ctx.phaseUuid].title }}</dd>
      {%- endif -%}

      {%- if ctx.createdBy -%}
      <dt>Created by</dt>
      <dd><span class="contact-name">{{ ctx.createdBy.firstName }} {{ ctx.createdBy.lastName }}</span> ({{ macros.formatEmail(ctx.createdBy.email) }}){% if ctx.createdBy.affiliation %}<br>{{ ctx.createdBy.affiliation }}{% endif %}</dd>
      {%- endif -%}

      {% for version in ctx.questionnaireVersions if version.uuid == ctx.questionnaireVersion %}
      <dt>Version</dt>
      <dd>{{version.name}} ({{ version.createdAt|datetime_format("%d %b %Y") }})</dd>
      {% endfor %}

      <dt>Generated on</dt>
      <dd>{{ ctx.createdAt|datetime_format("%d %b %Y") }}</dd>
    </dl>
    <p class="dsw">
      Data Management Plan created in Data Stewardship Wizard &laquo;<a href="https://ds-wizard.org" target="_blank">ds-wizard.org</a>&raquo;
    </p>
  </div>
</header>
","fileName":"parts/header.html.j2"},{"templateId":"dsw:science-europe:1.5.0","uuid":"e232390a-8b14-41eb-8c5d-20ea05f63e38","content":"<div id="q-how-data" class="question">
  <h3>2. How will the data be collected or created?</h3>

  <div class="answer">


  {%- set measuredPath = [uuids.creatingCUuid, uuids.measuredQUuid]|reply_path -%}
  {%- set measuredAUuid = repliesMap[measuredPath]|reply_str_value -%}
  {%- if measuredAUuid == uuids.measuredYesAUuid -%}
    {# Measured datasets #}
    {%- set measuredDataPath = [measuredPath, uuids.measuredYesAUuid, uuids.measuredDataQUuid]|reply_path -%}
    {%- set measuredDataItems = repliesMap[measuredDataPath]|reply_items -%}
    {%- if measuredDataItems|length > 0 -%}
      <h4>Instrument datasets</h4>
      <ul>
        {%- for datasetItem in measuredDataItems -%}
          {%- set pathPrefix = [measuredDataPath, datasetItem]|reply_path -%}
          <li>
            {%- set measuredDataNamePath = [pathPrefix, uuids.measuredDataNameQUuid]|reply_path -%}
            {%- set measuredDataName = repliesMap[measuredDataNamePath]|reply_str_value -%}
            <strong>{{ measuredDataName if measuredDataName else "(no name given)" }}</strong>
            {# Instruments #}
            {%- set measuredDataInstrPath = [pathPrefix, uuids.measuredDataInstrQUuid]|reply_path -%}
            {%- set measuredDataInstrItems = repliesMap[measuredDataInstrPath]|reply_items -%}
            {%- if measuredDataInstrItems|length > 0 -%}
              <p>For this dataset, we are using the following instruments:
              <ul>
                {%- for instrItem in measuredDataInstrItems -%}
                  {%- set instrPathPrefix = [measuredDataInstrPath, instrItem]|reply_path -%}
                  <li>
                    {%- set measuredDataInstrNamePath = [instrPathPrefix, uuids.measuredDataInstrNameQUuid]|reply_path -%}
                    {%- set measuredDataInstrName = repliesMap[measuredDataInstrNamePath]|reply_str_value -%}
                    {%- set measuredDataInstrDescPath = [instrPathPrefix, uuids.measuredDataInstrDescQUuid]|reply_path -%}
                    {%- set measuredDataInstrDesc = repliesMap[measuredDataInstrDescPath]|reply_str_value -%}
                    <strong>{{ measuredDataInstrName }}</strong>
                    {% if measuredDataInstrName and measuredDataInstrDesc -%}<span class="separator">&ndash;</span>{%- endif %}
                    {{ measuredDataInstrDesc }}
                  </li>
                {%- endfor -%}
              </ul>
              </p>
            {%- else -%}
              <p>No instruments for this dataset have been specified.</p>
            {%- endif -%}
            {# Quality #}
            {%- set measuredDataQualityPath = [pathPrefix, uuids.measuredDataQualityQUuid]|reply_path -%}
            {%- set measuredDataQualityAUuid = repliesMap[measuredDataQualityPath]|reply_str_value -%}
            {%- if measuredDataQualityAUuid == uuids.measuredDataQualityNoAUuid -%}
              <p>We will not be using quality process for this dataset.</p>
            {%- elif measuredDataQualityAUuid == uuids.measuredDataQualityYesAUuid -%}
              {# Named quality processes #}
              {%- set mdqPrefix = [measuredDataQualityPath, uuids.measuredDataQualityYesAUuid]|reply_path -%}
              {%- set calibratingAUuid = repliesMap[[mdqPrefix, uuids.mdQualityCalibatingQUuid]|reply_path]|reply_str_value -%}
              {%- set repetitionAUuid = repliesMap[[mdqPrefix, uuids.mdQualityRepetitionQUuid]|reply_path]|reply_str_value -%}
              {%- set standardizedAUuid = repliesMap[[mdqPrefix, uuids.mdQualityStandardizedQUuid]|reply_path]|reply_str_value -%}
              {%- set validationAUuid = repliesMap[[mdqPrefix, uuids.mdQualityValidationQUuid]|reply_path]|reply_str_value -%}
              {%- set peerReviewAUuid = repliesMap[[mdqPrefix, uuids.mdQualityPeerReviewQUuid]|reply_path]|reply_str_value -%}
              {%- set vocabulariesAUuid = repliesMap[[mdqPrefix, uuids.mdQualityVocabulariesQUuid]|reply_path]|reply_str_value -%}
              {%- set calibrating = calibratingAUuid == uuids.mdQualityCalibatingYesAUuid -%}
              {%- set repetition = repetitionAUuid == uuids.mdQualityRepetitionYesAUuid -%}
              {%- set standardized = standardizedAUuid == uuids.mdQualityStandardizedYesAUuid -%}
              {%- set validation = validationAUuid == uuids.mdQualityValidationYesAUuid -%}
              {%- set peerReview = peerReviewAUuid == uuids.mdQualityPeerReviewYesAUuid -%}
              {%- set vocabularies = vocabulariesAUuid == uuids.mdQualityVocabulariesYesAUuid -%}
              {%- set qualityProcesses = [calibrating, repetition, standardized, validation, peerReview, vocabularies] -%}
              {%- set qualityCount = qualityProcesses|select("true")|list|length -%}
              {%- if qualityCount == 1 -%}
                <p>
                  We will be using
                  {% if calibrating -%}
                    calibrating measurements
                  {%- endif -%}
                  {%- if repetition -%}
                    repeat samples/measurements
                  {%- endif -%}
                  {%- if standardized -%}
                    standardized data capture/recording
                  {%- endif -%}
                  {%- if validation -%}
                    <em>Data Entry</em> validation
                  {%- endif -%}
                  {%- if peerReview -%}
                    data peer review
                  {%- endif -%}
                  {%- if vocabularies -%}
                    controlled vocabularies
                  {%- endif %}
                  quality process.
                </p>
              {%- elif qualityCount > 1 -%}
                <p>
                We will be using the following quality processes:
                <ul>
                  {%- if calibrating -%}
                    <li>calibrating measurements</li>
                  {%- endif -%}
                  {%- if repetition -%}
                    <li>repeat samples/measurements</li>
                  {%- endif -%}
                  {%- if standardized -%}
                    <li>standardized data capture/recording</li>
                  {%- endif -%}
                  {%- if validation -%}
                    <li><em>Data Entry</em> validation</li>
                  {%- endif -%}
                  {%- if peerReview -%}
                    <li>data peer review</li>
                  {%- endif -%}
                  {%- if vocabularies -%}
                    <li>controlled vocabularies</li>
                  {%- endif -%}
                </ul>
                </p>
              {%- else -%}
                <p>We will be using quality processes.<p>
              {%- endif -%}
              {# Other quality processes #}
              {%- set mdQualityOtherPath = [mdqPrefix, uuids.mdQualityOtherQUuid]|reply_path -%}
              {%- set mdQualityOtherAUuid = repliesMap[mdQualityOtherPath]|reply_str_value -%}
              {%- if mdQualityOtherAUuid == uuids.mdQualityOtherYesAUuid -%}
                {%- set mdQualityOtherYesPath = [mdQualityOtherPath, mdQualityOtherYesAUuid, mdQualityOtherYesQUuid]|reply_path -%}
                {%- set mdQualityOther = repliesMap[mdQualityOtherYesPath]|reply_str_value -%}
                {%- if mdQualityOther -%}
                  <p>{{ "Additional" if qualityCount > 0 else "Our" }} quality processes are: {{ mdQualityOther|dot }}</p>
                {%- endif -%}
              {%- endif -%}
            {%- endif -%}
          </li>
        {%- endfor -%}
      </ul>
    {%- endif -%}
  {%- else -%}
    <p>There will be no instrument dataset in this project.</p>
  {%- endif -%}

  {# Storage and file conventions #}
  {%- set storageConvPath = [uuids.processingCUuid, uuids.storageConvQUuid]|reply_path -%}
  {%- set storageConvAUuid = repliesMap[storageConvPath]|reply_str_value -%}
  {%- if storageConvAUuid == uuids.storageConvExploreAUuid -%}
    {%- set stConvPrefix = [storageConvPath, uuids.storageConvExploreAUuid]|reply_path -%}
    {%- set storageConvFSysPath = [stConvPrefix, uuids.storageConvFSysQUuid]|reply_path -%}
    {%- set storageConvFSysAUuid = repliesMap[storageConvFSysPath]|reply_str_value -%}
    {%- set storageConvObjStorePath = [stConvPrefix, uuids.storageConvObjStoreQUuid]|reply_path -%}
    {%- set storageConvObjStoreAUuid = repliesMap[storageConvObjStorePath]|reply_str_value -%}
    {%- set storageConvRelDbPath = [stConvPrefix, uuids.storageConvRelDbQUuid]|reply_path -%}
    {%- set storageConvRelDbAUuid = repliesMap[storageConvRelDbPath]|reply_str_value -%}
    {%- set storageConvGraphDbPath = [stConvPrefix, uuids.storageConvGraphDbQUuid]|reply_path -%}
    {%- set storageConvGraphDbAUuid = repliesMap[storageConvGraphDbPath]|reply_str_value -%}
    {%- set storageConvTripleStorePath = [stConvPrefix, uuids.storageConvTripleStoreQUuid]|reply_path -%}
    {%- set storageConvTripleStoreAUuid = repliesMap[storageConvTripleStorePath]|reply_str_value -%}
    {%- if storageConvFSysAUuid or storageConvObjStoreAUuid or storageConvRelDbAUuid or storageConvGraphDbAUuid or storageConvTripleStoreAUuid -%}
      <h4>Storage and file conventions</h4>
      {# File System #}
      <p>
      {%- if storageConvFSysAUuid == uuids.storageConvFSysNoAUuid -%}
        We will not use a filesystem with files and folders.
      {%- elif storageConvFSysAUuid == uuids.storageConvFSysYesAUuid -%}
        {%- set scFSysPrefix = [storageConvFSysPath, uuids.storageConvFSysYesAUuid]|reply_path -%}
        {# Folder types #}
        {%- set scFSysSubjFoldersPath = [scFSysPrefix, uuids.scFSysSubjFoldersQUuid]|reply_path -%}
        {%- set scFSysSubjFoldersAUuid = repliesMap[scFSysSubjFoldersPath]|reply_str_value -%}
        {%- set scFSysAnalysisFoldersPath = [scFSysPrefix, uuids.scFSysAnalysisFoldersQUuid]|reply_path -%}
        {%- set scFSysAnalysisFoldersAUuid = repliesMap[scFSysAnalysisFoldersPath]|reply_str_value -%}
        {%- set scFSysWorkflowStepFoldersPath = [scFSysPrefix, uuids.scFSysWorkflowStepFoldersQUuid]|reply_path -%}
        {%- set scFSysWorkflowStepFoldersAUuid = repliesMap[scFSysWorkflowStepFoldersPath]|reply_str_value -%}
        {%- set subjectFolders = scFSysSubjFoldersAUuid == uuids.scFSysSubjFoldersYesAUuid -%}
        {%- set analysisFolders = scFSysAnalysisFoldersAUuid == uuids.scFSysAnalysisFoldersYesAUuid -%}
        {%- set workflowStepFolders = scFSysWorkflowStepFoldersAUuid == uuids.scFSysWorkflowStepFoldersYesAUuid -%}
        {%- set folders = [subjectFolders, analysisFolders, workflowStepFolders] -%}
        {%- if folders|any -%}
          We will use a filesystem with files and folders with the following folder conventions:
          <ul>
            {%- if subjectFolders -%}
              {%- set scFSysSubjFoldersConvs = repliesMap[[scFSysSubjFoldersPath, uuids.scFSysSubjFoldersYesAUuid, uuids.scFSysSubjFoldersConvsQUuid]|reply_path]|reply_str_value -%}
              <li>
                There will be a <strong>folder for each sample/subject</strong>.
                {% if scFSysSubjFoldersConvs %}
                  Each of those will use the following conventions: {{ scFSysSubjFoldersConvs|dot }}
                {%- endif -%}
              </li>
            {%- endif -%}
            {%- if analysisFolders -%}
              {%- set scFSysAnalysisFoldersConvs = repliesMap[[scFSysAnalysisFoldersPath, uuids.scFSysAnalysisFoldersYesAUuid, uuids.scFSysAnalysisFoldersConvsQUuid]|reply_path]|reply_str_value -%}
              <li>
                There will be  a <strong>(sub)folder for each (repeated) analysis</strong>. 
                {% if scFSysAnalysisFoldersConvs %}
                  Each of those will use the following conventions: {{ scFSysAnalysisFoldersConvs|dot }}
                {%- endif -%}
              </li>
            {%- endif -%}
            {%- if workflowStepFolders -%}
              {%- set scFSysWorkflowStepFoldersConvs = repliesMap[[scFSysWorkflowStepFoldersPath, uuids.scFSysWorkflowStepFoldersYesAUuid, uuids.scFSysWorkflowStepFoldersConvsQUuid]|reply_path]|reply_str_value -%}
              <li>
                There will be a <strong>(sub)folder for each step in the analysis workflow</strong>.
                {% if scFSysWorkflowStepFoldersConvs %}
                  Each of those will use the following conventions: {{ scFSysWorkflowStepFoldersConvs|dot }}
                {%- endif -%}
              </li>
            {%- endif -%}
          </ul>
        {%- else -%}
          We will use a filesystem with files and folders.
        {%- endif -%}
        {# Other appointments #}
        {%- set scFSysAppointmentsPath = [scFSysPrefix, uuids.scFSysAppointmentsQUuid]|reply_path -%}
        {%- set scFSysAppointments = repliesMap[scFSysAppointmentsPath]|reply_str_value -%}
        {%- if scFSysAppointments %}
          {{ "Moreover, we" if folders|any else "We" }} have made appointments about naming the files. {{ appointmentsText|capitalize|dot }}
        {%- endif -%}
      {%- endif -%}
      </p>
      {# Object Store #}
      <p>
      {%- if storageConvObjStoreAUuid == uuids.storageConvObjStoreNoAUuid -%}
        We will not be storing data in an "object store" system.
      {%- elif storageConvObjStoreAUuid == uuids.storageConvObjStoreYesAUuid -%}
        {%- set scObjStoreNamingQUuid = [storageConvObjStorePath, uuids.storageConvObjStoreYesAUuid, uuids.scObjStoreNamingQUuid]|reply_path -%}
        {%- set scObjStoreNaming = repliesMap[scObjStoreNamingQUuid]|reply_str_value -%}
        {%- if scObjStoreNaming -%} 
          We will be storing data in an "object store" system using following naming conventions for objects in the store: {{ scObjStoreNaming|dot }}
        {%- else -%}
          We will be storing data in an "object store" system.
        {%- endif -%}
      {%- endif -%}
      </p>
      {# Relational Database #}
      <p>
      {%- if storageConvRelDbAUuid == uuids.storageConvRelDbNoAUuid -%}
        We will not use a relational database system to store project data.
      {%- elif storageConvRelDbAUuid == uuids.storageConvRelDbYesAUuid -%}
        We will use a relational database system to store project data.
        {%- set scRelDbHandleChangesPath = [storageConvRelDbPath, uuids.storageConvRelDbYesAUuid, uuids.scRelDbHandleChangesQUuid]|reply_path -%}
        {%- set scRelDbHandleChangesAUuid = repliesMap[scRelDbHandleChangesPath]|reply_str_value -%}
        {%- if scRelDbHandleChangesAUuid == uuids.scRelDbReadOnlyAnswerUuid %}
          Data in the database will be read-only.
        {%- elif scRelDbHandleChangesAUuid == uuids.scRelDbCrudAnswerUuid %}
          We will be allowing Create, Update and Delete operations for data in the database.
        {%- elif scRelDbHandleChangesAUuid == uuids.scRelDbExpireAnswerUuid %}
          Modifications will be made by <em>Expiring</em> the existing data and <em>Adding</em> updated data.
        {%- endif -%}
      {%- endif -%}
      </p>
      {# Graph Database #}
      <p>
      {%- if storageConvGraphDbAUuid == uuids.storageConvGraphDbNoAUuid -%}
        We will not use a graph database for data in the project.
      {%- elif storageConvGraphDbAUuid == uuids.storageConvGraphDbYesAUuid -%}
        We will use a graph database for data in the project.
      {%- endif -%}
      </p>
      {# Triple Store #}
      <p>
      {%- if storageConvTripleStoreAUuid == uuids.storageConvTripleStoreNoAUuid -%}
        We will not be storing data in a triple store.
      {%- elif storageConvTripleStoreAUuid == uuids.storageConvTripleStoreYesAUuid -%}
        We will be storing data in a triple store.
      {%- endif -%}
      </p>
    {%- endif -%}
  {%- endif -%}
  </div>
</div>
","fileName":"parts/questions/02-how-data.html.j2"},{"templateId":"dsw:science-europe:1.5.0","uuid":"b5ef5acd-8688-4a20-9172-b7d0649104c8","content":"<div id="q-ethical-issues" class="question">
  <h3>5. How will you manage copyright and Intellectual Property Rights (IPR) issues?</h3>

  <div class="answer">
    {# Open philosophy #}
    {%- set asOpenAsPossiblePath = [uuids.accessCUuid, uuids.asOpenAsPossibleQUuid]|reply_path -%}
    {%- set asOpenAsPossibleAUuid = repliesMap[asOpenAsPossiblePath]|reply_str_value -%}
    {%- if asOpenAsPossibleAUuid == uuids.asOpenAsPossibleYesAUuid -%}
      <p>We will be working with the philosophy <em>as open as possible</em> for our data.</p>
    {%- endif -%}

    {# Can open data #}
    {%- set openImmediatelyPath = [uuids.accessCUuid, uuids.openImmediatelyQUuid]|reply_path -%}
    {%- set openImmediatelyAUuid = repliesMap[openImmediatelyPath]|reply_str_value -%}
    {%- if openImmediatelyAUuid == uuids.openImmediatelyYesAUuid -%}
      <p>All of our data can become completely open immediately.</p>
    {%- elif openImmediatelyAUuid == uuids.openImmediatelyNoAUuid -%}
      {%- set pathPrefix = [openImmediatelyPath, uuids.openImmediatelyNoAUuid]|reply_path -%}
      {%- set notOpenLegalReasonsPath = [pathPrefix, uuids.notOpenLegalReasonsQUuid]|reply_path -%}
      {%- set notOpenLegalReasonsAUuid = repliesMap[notOpenLegalReasonsPath]|reply_str_value -%}
      {%- set notOpenBusinessReasonsPath = [pathPrefix, uuids.notOpenBusinessReasonsQUuid]|reply_path -%}
      {%- set notOpenBusinessReasonsAUuid = repliesMap[notOpenBusinessReasonsPath]|reply_str_value -%}
      {%- set notOpenOtherReasonsPath = [pathPrefix, uuids.notOpenOtherReasonsQUuid]|reply_path -%}
      {%- set notOpenOtherReasonsAUuid = repliesMap[notOpenOtherReasonsPath]|reply_str_value -%}
      {%- set notOpenBusinessReasonsOtherPath = [notOpenBusinessReasonsPath, uuids.notOpenBusinessReasonsOtherAUuid, uuids.notOpenBusinessReasonsOtherQUuid]|reply_path -%}
      {%- set notOpenBusinessReasonsOther = repliesMap[notOpenBusinessReasonsOtherPath]|reply_str_value -%}
      {%- set notOpenOtherReasonsOtherPath = [notOpenOtherReasonsPath, uuids.notOpenOtherReasonsOtherAUuid, uuids.notOpenOtherReasonsOtherQUuid]|reply_path -%}
      {%- set notOpenOtherReasonsOther = repliesMap[notOpenOtherReasonsOtherPath]|reply_str_value -%}

      {%- set legalReasons = notOpenLegalReasonsAUuid == uuids.notOpenLegalReasonsYesAUuid -%}
      {%- set businessReasonsPatents = notOpenBusinessReasonsAUuid == uuids.notOpenBusinessReasonsPatentsAUuid -%}
      {%- set businessReasonsOther = notOpenBusinessReasonsAUuid == uuids.notOpenBusinessReasonsOtherAUuid -%}
      {%- set otherReasonsPapers = notOpenOtherReasonsAUuid == uuids.notOpenOtherReasonsPapersAUuid -%}
      {%- set otherReasonsOther = notOpenOtherReasonsAUuid == uuids.notOpenOtherReasonsOtherAUuid -%}
      {%- set reasons = [legalReasons, businessReasonsPatents, businessReasonsOther, otherReasonsPapers, otherReasonsOther] -%}
      {%- set nReasons = reasons|select("true")|list|length -%}

      {%- if nReasons > 0 -%}
        <p>
        The data cannot become completely open immediately because 
        {%- if nReasons == 1 -%}
          {%- if legalReasons %}
            of legal reasons.
          {%- elif businessReasonsPatents %}
            of patent-related business reasons.
          {%- elif businessReasonsOther %}
            of non-patent business reasons{{  ": " ~ notOpenBusinessReasonsOther|dot if notOpenBusinessReasonsOther else "." }}
          {%- elif otherReasonsPapers %}
            we want to publish a paper first.
          {%- elif otherReasonsOther %}
            we have other than paper-publishing reasons{{ ": " ~ notOpenOtherReasonsOther|dot if notOpenOtherReasonsOther else "." }}
          {%- endif -%}
        {%- else %}
          of:
          <ul>
            {%- if legalReasons %}
              <li>legal reasons</li>
            {%- endif -%}
            {%- if businessReasonsPatents %}
              <li>patent-related business reasons</li>
            {%- elif businessReasonsOther %}
              <li>non-patent business reasons{{ ": " ~ notOpenBusinessReasonsOther if notOpenBusinessReasonsOther else "" }}</li>
            {%- endif -%}
            {%- if otherReasonsPapers %}
              <li>we want to publish a paper first</li>
            {%- elif otherReasonsOther -%}
              <li>we have other than paper-publishing reasons{{ ": " ~ notOpenOtherReasonsOther if notOpenOtherReasonsOther else "" }}</li>
            {%- endif -%}
          </ul>
        {%- endif -%}
      {%- else -%}
        <p>The data cannot become completely open immediately.</p>
      {%- endif -%}

      {%- set notOpenLimitedEmbargoPath = [openImmediatelyPath, uuids.openImmediatelyNoAUuid, uuids.notOpenLimitedEmbargoQUuid]|reply_path -%}
      {%- set notOpenLimitedEmbargoAUuid = repliesMap[notOpenLimitedEmbargoPath]|reply_str_value -%}
      {%- if notOpenLimitedEmbargoAUuid == uuids.notOpenLimitedEmbargoIndefinitelyAUuid -%}
        <p>Limited embargo cannot be used because some restricted data will be embargoed indefinitely.</p>
      {%- elif notOpenLimitedEmbargoAUuid == uuids.notOpenLimitedEmbargoReleasedSoonAUuid -%}
        <p>Data will be released only as soon as restrictions are falling away.</p>
      {%- elif notOpenLimitedEmbargoAUuid == uuids.notOpenLimitedEmbargoYesAUuid -%}
        <p>Data that is not legally restrained will be released after a fixed time period, unconditionally.</p>
      {%- endif -%}
    {%- endif -%}

    {# Ownership #}
    {%- set ownershipPath = [uuids.creatingCUuid, uuids.ownershipQUuid]|reply_path -%}
    {%- set ownershipAUuid = repliesMap[ownershipPath]|reply_str_value -%}
    {%- if ownershipAUuid == uuids.ownershipNoOwnerAUuid -%}
      <p>Our data is legally not copyrightable, there is no legal owner.</p>
    {%- elif ownershipAUuid == uuids.ownershipPIAUuid -%}
      <p>All data will be owned by the Principal Investigator.</p>
    {%- elif ownershipAUuid == uuids.ownershipInstituteAUuid -%}
      <p>All data will be owned by the institute.</p>
    {%- elif ownershipAUuid == uuids.ownershipConsortiumAUuid -%}
      <p>We have a consortium agreement that arranges Intellectual Property.</p>
    {%- elif ownershipAUuid == uuids.ownershipOtherAUuid -%}
      {%- set ownershipOtherPath = [ownershipPath, uuids.ownershipOtherAUuid, uuids.ownershipOtherQUuid]|reply_path -%}
      {%- set ownershipOther = repliesMap[ownershipOtherPath]|reply_str_value -%}
      {%- if ownershipOther -%}
        <p>We have made the following arrangements regarding the data ownership: {{ ownershipOther|dot }}</p>
      {%- endif -%}
    {%- endif -%}

    {# (Non)reference data - license #}
    {%- set preexistingPath = [uuids.reusingCUuid, uuids.preexistingQUuid]|reply_path -%}
    {%- set preexistingAUuid = repliesMap[preexistingPath]|reply_str_value -%}
    {%- if preexistingAUuid == uuids.preexistingYesAUuid -%}
      {%- set usingPreexistingPath = [preexistingPath, uuids.preexistingYesAUuid, uuids.usingPreexistingQUuid]|reply_path -%}
      {%- set usingPreexistingAUuid = repliesMap[usingPreexistingPath]|reply_str_value -%}
      {%- if usingPreexistingAUuid == uuids.usingPreexistingYesAUuid -%}
        {%- set refDataPath = [usingPreexistingPath, uuids.usingPreexistingYesAUuid, uuids.refDataQUuid]|reply_path -%}
        {%- set refDataItems = repliesMap[refDataPath]|reply_items -%}
        {%- set nrefDataPath = [usingPreexistingPath, uuids.usingPreexistingYesAUuid, uuids.nrefDataQUuid]|reply_path -%}
        {%- set nrefDataItems = repliesMap[nrefDataPath]|reply_items -%}
        {%- set answeredRefData = [] -%}
        {%- for i in refDataItems -%}
          {%- if repliesMap[[refDataPath, i, uuids.refDataConditionsQUuid]|reply_path] -%}
            {%- do answeredRefData.append(i) -%}
          {%- endif -%}
        {%- endfor -%}
        {%- set answeredNRefData = [] -%}
        {%- for i in nrefDataItems -%}
          {%- if repliesMap[[nrefDataPath, i, uuids.nrefDataConditionsQUuid]|reply_path] -%}
            {%- do answeredNRefData.append(i) -%}
          {%- endif -%}
        {%- endfor -%}
        {%- if (answeredRefData|length + answeredNRefData|length) > 0 -%}
          <p>For the reference and non-reference data sets that we reuse, conditions are as follows:
          <ul>
            {%- for i in answeredRefData -%}
              {%- set refDataNamePath = [refDataPath, i, uuids.refDataNameQUuid]|reply_path -%}
              {%- set refDataNameReply = repliesMap[refDataNamePath] -%}
              {%- set refDataConditionsPath = [refDataPath, i, uuids.refDataConditionsQUuid]|reply_path -%}
              {%- set refDataConditionsAUuid = repliesMap[refDataConditionsPath]|reply_str_value -%}
              {%- if refDataConditionsAUuid -%}
                <li>
                  <strong>{{ macros.integrationValue(refDataNameReply, uuids.refDataNameQUuid) }}</strong>
                  <span class="separator">&ndash;</span>
                  {% if refDataConditionsAUuid == uuids.refDataConditionsCC0AUuid -%}
                    freely available for any use (public domain or CC0).
                  {%- elif refDataConditionsAUuid == uuids.refDataConditionsCCBYAUuid -%}
                    freely available with obligation to quote the source (e.g. CC-BY).
                  {%- elif refDataConditionsAUuid == uuids.refDataConditionsOtherAUuid -%}
                    {%- set refDataConditionsOtherPath = [refDataConditionsPath, uuids.refDataConditionsOtherAUuid, uuids.refDataConditionsOtherQUuid]|reply_path -%}
                    {%- set refDataConditionsOther = repliesMap[refDataConditionsOtherPath]|reply_str_value -%}
                    available under specific restrictions, which we will follow in our project{{ ": " ~ refDataConditionsOther|dot if refDataConditionsOther else "." }}
                  {%- endif -%}
                </li>
              {%- endif -%}
            {%- endfor -%}
            {%- for i in answeredNRefData -%}
              {%- set nrefDataNamePath = [nrefDataPath, i, uuids.nrefDataNameQUuid]|reply_path -%}
              {%- set nrefDataNameReply = repliesMap[nrefDataNamePath] -%}
              {%- set nrefDataConditionsPath = [nrefDataPath, i, uuids.nrefDataConditionsQUuid]|reply_path -%}
              {%- set nrefDataConditionsAUuid = repliesMap[nrefDataConditionsPath]|reply_str_value -%}
              {%- if nrefDataConditionsAUuid -%}
                <li>
                  <strong>{{ macros.integrationValue(nrefDataNameReply, uuids.nrefDataNameQUuid) }}</strong>
                  <span class="separator">&ndash;</span>
                  {% if nrefDataConditionsAUuid == uuids.nrefDataConditionsCC0AUuid -%}
                    freely available for any use (public domain or CC0).
                  {%- elif nrefDataConditionsAUuid == uuids.nrefDataConditionsCCBYAUuid -%}
                    freely available with obligation to quote the source (e.g. CC-BY).
                  {%- elif nrefDataConditionsAUuid == uuids.nrefDataConditionsOtherAUuid -%}
                    {%- set nrefDataConditionsOtherPath = [nrefDataConditionsPath, uuids.nrefDataConditionsOtherAUuid, uuids.refDataConditionsOtherQUuid]|reply_path -%}
                    {%- set nrefDataConditionsOther = repliesMap[nrefDataConditionsOtherPath]|reply_str_value -%}
                    available under specific restrictions, which we will follow in our project{{ ": " ~ nrefDataConditionsOther|dot if nrefDataConditionsOther else "." }}
                  {%- endif -%}
                </li>
              {%- endif -%}
            {%- endfor -%}
          </ul>
        {%- endif -%}
      {%- endif -%}
    {%- endif -%}
  </div>
</div>
","fileName":"parts/questions/05-copyright-ipr.html.j2"},{"templateId":"dsw:science-europe:1.5.0","uuid":"b40b200a-9154-4546-b03e-fa3dfb97f76b","content":"<div id="dmp-content">
